//@note:@shyft:
// updated for solidity 7.1
// added shyft inflation values & appropriate mapping for reward structures


// Example block reward contract.

pragma solidity ^0.7.1;

import "../Interfaces/IBlockReward.sol";

//0x5B38Da6a701c568545dCfcB03FcB875f56beddC4
//0xAb8483F64d9C6d1EcF9b849Ae677dD3315835cb2
//0x4B20993Bc481177ec7E8f571ceCaE8A9e22C02db
//0x78731D3Ca6b7E34aC0F824c42a7cC18A495cabaB
//["0x5B38Da6a701c568545dCfcB03FcB875f56beddC4","0xAb8483F64d9C6d1EcF9b849Ae677dD3315835cb2","0x4B20993Bc481177ec7E8f571ceCaE8A9e22C02db","0x78731D3Ca6b7E34aC0F824c42a7cC18A495cabaB"], [1,2,3,4]
//["0x5B38Da6a701c568545dCfcB03FcB875f56beddC4"], [1]
contract ExampleBlockReward is IBlockReward {
    address systemAddress;

    uint runtimeInflationReward = 7.45814307 ether;
    uint stakingPoolReward = 7.45814307 ether;

    uint sealerReward = 7.45814307 ether;
    uint emptyStepReward = 7.45814307 ether;
    uint externalReward = runtimeInflationReward + stakingPoolReward; // for runtime & staking
    uint uncleReward = 0 ether;

    uint[] steps = [0, 1, 2, 3];
    uint[] stepValues = [sealerReward, emptyStepReward, externalReward, uncleReward];

    mapping(uint=>uint) stepMap;

    modifier onlySystem() {
        require(msg.sender == systemAddress);
        _;
    }

    constructor(address _systemAddress)
    {
        /* systemAddress = 0xffffFFFfFFffffffffffffffFfFFFfffFFFfFFfE; */
        systemAddress = _systemAddress;

        for (uint i = 0; i < steps.length; i++) {
            stepMap[i] = stepValues[i];
        }
    }

    // produce rewards for the given benefactors, with corresponding reward codes.
    // only callable by `SYSTEM_ADDRESS`
    function reward(address[] memory benefactors, uint16[] memory kind)
    external
    override
    view
    onlySystem
    returns (address[] memory, uint256[] memory)
    {
        require(benefactors.length == kind.length);
        uint256[] memory rewards = new uint256[](benefactors.length);

        for (uint i = 0; i < rewards.length; i++) {
            if (stepMap[i] != 0) {
                rewards[i] = stepMap[i];
            }
        }

        return (benefactors, rewards);
    }
}
