pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

interface IShyftBlockRewardAdministration {
    function getSystemAddress() external view returns (address result);

    function setExAdministrator(address _administratorAddress) external returns (bool result);
    function removeExAdministratorPermanently() external returns (bool result);

    function setExRewardsEnabled(bool _exRewardsEnabled) external returns (bool result);
    function setExCallsEnabled(bool _exCallsEnabled) external returns (bool result);
    function setExContractAddress(address _exContractAddress) external returns (bool result);
    function setupExData(bool _exRewardsEnabled, bool _exCallsEnabled, address _exContractAddress) external returns (bool result);

    function getExternalContractAddress() external view returns (address result);
    function getExternalContractReward() external view returns (uint result);

    function setBenefactorDelegate(address _benefactorAddress, address _delegateAddress) external returns (uint result);
    function setBenefactorCoinbase(address _benefactorAddress, address _coinbaseAddress) external returns (uint result);

    function getBenefactorDelegate(address _benefactorAddress) external view returns (address result);
    function getBenefactorCoinbase(address _benefactorAddress) external view returns (address result);

    function getDelegatedBenefactorAddress(address _someAddress) external view returns (address result);

    function getBlockSealerHistoricallyExists(address _blockSealerAddress) external view returns (bool result);
    function getHistoricalBlockSealers() external view returns (address[] memory result);
    function getBlockSealerLastBlock(address _blockSealerAddress) external view returns (uint256 result);
}
