pragma solidity ^0.7.1;
pragma experimental ABIEncoderV2;
//SPDX-License-Identifier: UNLICENSED

interface IShyftConduit {
    /// @dev This is set up such that the distribution amounts (per block) can be set up with a (whitelisted) address.
    struct ConterminousInfo {
        address ownAddress;

        bool whiteListed;

        uint256 whiteShyftedDistributionPercent_leftShifted24Bits;
        uint256 whiteShyftedBalance;

        uint256 lastBlockProcessed;

        //@note: forward linked list.
        address nextLink;
    }

    // linkages
    function setSystemDistributionContractAddress(address _systemDistributionContractAddress) external returns (uint8 result);
    function setShyftKycContractRegistryAddress(address _shyftKycContractRegistryAddress) external returns (uint8 result);
    function setShyftKycContractAddress(address _shyftKycContractAddress) external returns (uint8 result);

    // after detach
    function retrieveExternalFundsAfterDetach(address _recipient) external returns (uint8 result);

    // for manual conduit pushes (not updating from a block reward contract)
    function withdrawSafeFundsToSelfOnlyTrustRouted(uint256 _amount) external returns (uint8 result);

    // erc223 receiver return funds
    function callTokenReverseTransfer(uint256 _tokenIndex) external returns (uint8 result);

    // set up distribution amounts
    function setConterminousDistributionAmountsPermissions(bytes32 _distributionKeyKeccak) external returns (uint8 result);
    function setConterminousDistributionAmounts(address[] memory _conterminousAddresses,
                                                uint256[] memory _conterminousDistributionPercentages_leftShifted24Bits,
                                            //                                                uint256 _blockReward,
                                                bytes32 _distributionKeyKeccak) external returns (uint8 result);

    // personal integration of conterminous balance
    function integrateIndividualConterminousDistribution(address _conterminousAddress) external returns (uint256 result);

    // personal withdraw functions
    function withdrawIndividualConterminousDistributionToTrustedAccount(address _shyftKycContractAddress, uint256 _value, uint256 _gasToSend) external returns (uint8 result);
    function withdrawIndividualConterminousDistributionToNormalAccount(address payable _to, uint256 _value) external returns (bool ok);
    function withdrawIndividualConterminousDistributionToExternalContract(address _contractAddress, uint256 _value) external returns (bool ok);

    // conterminous info
    function checkConterminousWhiteShyftedBalance(address _address) external view returns (uint256 balance);
    function checkConterminousLastBlockProcessed(address _address) external view returns (uint256 lastBlockProcessed);
    function isValidConterminousAddress(address _conterminousAddress) external view returns (bool result);
    function getConterminousInfo(address _conterminousAddress) external view returns (ConterminousInfo memory result);
    function getAllConterminousInfos() external view returns (ConterminousInfo[] memory result);
    function getConterminousGeneration() external view returns(uint256 result);

    // prime revocation manager
    function setPrimeRevocationManagerAddress(address _primeRevocationManagerAddress) external returns (uint8 result);
    function isPrimeRevocationManager(address _primeRevocationManagerAddress) external view returns (bool result);

    // administration for block reward integration
    function voteAdminShouldIntegrateConterminousDistributionsOnUpdate(bool _shouldIntegrateOnDistributionOnUpdate) external returns (uint8 result);
    // administration for block reward update
    function voteAdminShouldUpdateBenefactor(bool _shouldUpdateBenefactor) external returns (uint8 result);
    function voteAdminShouldRemoveBenefactorPermanently(bool _shouldRemovePermanently) external returns (uint8 result);
    function voteAdminUpdateBenefactorAddressTarget(address _adminUpdateBenefactorAddressTarget) external returns (uint8 result);
 }
