pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "./Administrable.sol";

contract RelayerElection is Administrable {

    struct Relayer {
        address addr;
        bytes32 voteCommitment;
        bool voted;
        bool revealed;
        bool isInSet;
        uint count;
    }

    mapping (address => Relayer) relayerSet;
    address[] relayerAddrs;

    Relayer nextRelayer;

    uint8 votes; // the amount of votes that have been cast for this election
    uint8 reveals; // the amount of votes that have been revealed for this election

    event RelayerAdded(address _relayer);
    event SetVote(address indexed _voter, bytes32 _commitment);
    event Reveal(address indexed _voter, address indexed _elected);
    event ResetElection();
    event VotingComplete();
    event ElectionComplete();

    // @dev: Adds a relayer to this election. Can only be called by the address
    // that deployed the contract
    //
    // @param: address relayerToAdd, address of the relayer to add to the election
    function addRelayer(address relayerToAdd) public returns (bool){
         require(msg.sender == owner);
         require(!isInRelayerSet(relayerToAdd));

         Relayer memory relayer;
         relayer.addr = relayerToAdd;
         relayer.voteCommitment = keccak256("");
         relayer.voted = false;
         relayer.revealed = false;
         relayer.isInSet = true;
         relayer.count = 0;

         relayerSet[relayerToAdd] = relayer;
         relayerAddrs.push(relayerToAdd);
         emit RelayerAdded(relayerToAdd);
         return true;
    }

    function removeRelayer(address _addr) public returns (bool) {
        require(msg.sender == owner);
        require(isInRelayerSet(_addr));

        delete relayerSet[_addr];
        for(uint i = 0; i < numRelayers(); i++) {
            if (relayerAddrs[i] == _addr) {
                delete relayerAddrs[i];
                return true;
            }
        }

        return isInRelayerSet(_addr);
    }

    // @dev: A relayer sets their vote for a particular relayer as a commitment.
    //
    // @param: bytes32 commitment, commitment to a particular relayer as their vote
    function setVote(bytes32 commitment) public returns (bool) {
         require(isInRelayerSet(msg.sender));
         require(commitment != keccak256(""));
         require(commitment != keccak256(abi.encodePacked(msg.sender,msg.sender)));

         Relayer storage r = relayerSet[msg.sender];

         if(!r.voted) {
            votes++;
         }

         r.voteCommitment = commitment;
         r.voted = true;
         r.revealed = false;

         emit SetVote(msg.sender, commitment);

         // 2/3 of relayers must vote
         if (votes >= numRelayers() / 2) {
            emit VotingComplete();
            votes = 0;
        }

        return true;
    }

    // @dev: A relayer reveals their vote.
    //
    // @param: address relayerToElect, the address of their vote.
    function reveal(address relayerToElect) public returns (bool) {
        require(msg.sender != relayerToElect);
        require(isInRelayerSet(msg.sender));
        require(isInRelayerSet(relayerToElect));

        Relayer storage revealer = relayerSet[msg.sender];
        require(revealer.voted);
        require(revealer.voteCommitment == keccak256(abi.encodePacked(relayerToElect,msg.sender)));
        require(!revealer.revealed);

        revealer.voted = false;
        revealer.revealed = true;

        Relayer storage toBeElected = relayerSet[relayerToElect];
        toBeElected.count++;

        if(toBeElected.count > nextRelayer.count) {
            nextRelayer = toBeElected;
        }

        emit Reveal(msg.sender, relayerToElect);
        reveals++; 

        // 2/3 of relayers must reveal
        if (reveals >= numRelayers() / 2) {
            emit ElectionComplete();
            reveals = 0;
        }

        return true;
    }

    //@dev: After a certain threshold of relayers have submitted their votes,
    // get the validator for that epoch.
    function getElectedRelayer() public view returns (address) {
        return nextRelayer.addr;
    }

    function isInRelayerSet(address toCheck) public view returns (bool) {
        return relayerSet[toCheck].isInSet;
    }

    function numRelayers() public view returns (uint256) {
        return relayerAddrs.length;
    }

    function getRelayerAt(uint256 i) public view returns (address) {
        return relayerAddrs[i % numRelayers()];
    }

    function resetElection() public returns (bool) {
        require(msg.sender == owner);

        votes = 0;
        reveals = 0;

        for(uint i=0; i < relayerAddrs.length; i++) {
            relayerSet[relayerAddrs[i]].count = 0;
            relayerSet[relayerAddrs[i]].voteCommitment = 0;
            relayerSet[relayerAddrs[i]].voted = false;
            relayerSet[relayerAddrs[i]].revealed = false;
        }

        emit ResetElection();

        return true;
    }

}
