pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "./Libraries/SafeMath.sol";
import "./Libraries/MerklePatriciaProof.sol";

import "./Interfaces/IShyftCacheGraph.sol";

//import "./Interfaces/IShyftBridge.sol";
import "./Interfaces/ITrustChannelManager.sol";
import "./Interfaces/ITrustChannelManager_Routing.sol";

import "./Interfaces/IShyftCacheGraph_ExternalTrustChannelProver.sol";

/// @dev
/// | A reference contract related to the Shyft Cache Graph's routing channels.
/// | Especially useful if wanting to prove off-chain data (would need to be associated to the Shyft Bridge), in relation to offchain Trust Channels the user is associated to.

contract ShyftCacheGraph_ExternalTrustChannelProver is IShyftCacheGraph_ExternalTrustChannelProver {
    event EVT_ProveCacheGraphSegment(address _identifiedAddress, bytes32 _receiptsRoot);

    /// @dev using the SafeMath library for any uint256 values.
    using SafeMath for uint256;

    /// @dev The owner of this contract.
    address owner = address(0);

    /// @dev The Shyft Cache Graph address that this contract is bound to.
    address shyftCacheGraphAddress = address(0);

    /// @dev The Trust Anchor Manager address that this contract is bound to.
    address trustChannelManagerAddress = address(0);

    /// @dev Initializes this contract with the owner set to the calling public address.

    constructor() {
        owner = msg.sender;
    }

    /// @param _shyftCacheGraphAddress The smart contract address for the Shyft Cache Graph that should be linked.
    /// @dev Links the Shyft Cache Graph to this contract's function flow.
    /// @return result
    ///    | false: [ will never return false due to require() ]
    ///    | true: shyft cache graph address set correctly

    function setShyftCacheGraphAddress(address _shyftCacheGraphAddress) public returns (bool result) {
        require(_shyftCacheGraphAddress != address(0));
        require(msg.sender == owner);

        shyftCacheGraphAddress = _shyftCacheGraphAddress;

        //shyft cache graph address set correctly
        return true;
    }

    /// @param _trustChannelManagerAddress The smart contract address for the Trust Channel Manager that should be linked.
    /// @dev Links Trust Channel Manager to this contract's function flow.
    /// @return result
    ///    | false: [ will never return false due to require() ]
    ///    | true: trust channel manager address set correctly

    function setTrustChannelManagerAddress(address _trustChannelManagerAddress) public returns (bool result) {
        require(_trustChannelManagerAddress != address(0));
        require(msg.sender == owner);

        trustChannelManagerAddress = _trustChannelManagerAddress;

        //trust channel manager address set correctly
        return true;
    }

    function proveCacheGraphSegment(address _identifiedAddress,
                                    uint64 _segmentIndex,
                                    bytes32 _receiptsRoot,
                                    bytes memory _rlpCacheGraphProofTx,
                                    bytes memory _receiptPath,
                                    bytes memory _receiptParentNodes,
                                    bytes memory _rlpCacheGraphProofReceipt) public override returns (uint8 result) {
        // need to extend util/txProof.js to generate proofs for receipts trie as well as tx trie
        bool receiptProofResult = MerklePatriciaProof.verify(_rlpCacheGraphProofReceipt, _receiptPath, _receiptParentNodes, _receiptsRoot);

        //@note:@todo: get block number for the receipts root, check if that block number is higher than the existing confirmed status
        // if so, run the proof checking against the current cache graph segment (should be external to this blockchain at this point, but should also pass on a local blockchain update)

        if (receiptProofResult == true) {
            bytes32 rlpCacheGraphProofTxHash = keccak256(abi.encodePacked(_rlpCacheGraphProofTx));
            //@note:@todo: if that passes, update the user's cache graph data with the details surrounding this, and return true.

//            //get the contract address from the rules storage, this is all that is needed to perform the next step
//            address exDataContractAddress = address(uint160(uint256(trustChannelManager.getTrustChannelRulesStorage(_trustChannelIndex, uint8(trustChannelRulesEnum.ExternalRoutingContractEnforced), 0))));
//
//            bytes4 sig = bytes4(keccak256(abi.encodePacked("proveCacheGraphSegment(address,uint64,bytes32,bytes,bytes,bytes,bytes)")));
//
//            bytes memory result;
//            (success, result) = exDataContractAddress.call(abi.encodeWithSelector(sig, _identifiedAddress, _segmentIndex, _receiptsRoot, _rlpCacheGraphProofTx, _receiptPath, _receiptParentNodes, _rlpCacheGraphProofReceipt));
//            //check whether the rules' requirement for amount sent to be under or equal to this amount sent.
//            //                        (_amount, _receiver, _tokenLeadContract, _expectedAmount, _expectedSC) = abi.decode(data, (uint, address, address, uint, address));
//            int16 exDataExternalTrustLevel;
//            (externalTrustLevel, exDataExternalTrustLevel) = abi.decode(result, (int16, int16));

//            Withdrawal storage userWithdrawal = userWithdrawals[msg.sender][rlpCacheGraphProofTxHash];
//            userWithdrawal.receiptsRoot = _receiptsRoot;

            emit EVT_ProveCacheGraphSegment(_identifiedAddress, _receiptsRoot);

            return 1;
        } else {
            return 0;
        }
    }
}

