// pragma experimental ABIEncoderV2;

pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "./Interfaces/ITrustAnchorManager.sol";
import "./Interfaces/IShyftConduit.sol";

import "./ECRecovery.sol";
import "./Administrable.sol";

/// @author Chris Forrester, 2018+ Shyft. all rights reserved.

/// @dev
/// | The Trust Anchor Manager is the basic building block for the associative mechanism of data holders (Trust Anchors) to end users.
/// | This contract manages the Trust Anchors themselves, with sparse data points (mainly Jurisdiction) that are required for a top-level directory service.

/// | Administrators of this contract are able to be set in order to allow for better management practices, with multi-signature support from the Administrable contract.


//@note:
// patterns used:
// owner pattern.
// administrator pattern.

// irregular usages:
//
// (1) structure:
// hash table based mirrored data accessors for maximum read with a bit of write overhead.
// (1) rationale:
// most contracts depend on this and keeping two hash tables in memory constantly when > 90% of transactions should eventually be using the kyc
// database to at least trigger friendly events re: reputation token is a useful thing.

// compatibility:
// fully ethereum EVM compliant.

// future work:
// research into cost/benefit of upgradability.
// further caching and improvement of read performance/cost.


contract TrustAnchorManager is Administrable, ITrustAnchorManager {
    /// @dev Event for a Trust Anchor requiring multisignature permission before a Jurisdiction change.
    event EVT_JurisdictionDoesRequireChange( address trustAnchorAddress );
    /// @dev Event for getting the Jurisdiction for a Trust Anchor.
    event EVT_GettingJurisdictionFromTrustAnchor( uint16 jurisdiction, address trustAnchorAddress );

    /// @dev Event for setting up a generic Trust Anchor's ExtraData fields.
    event EVT_setGenericTrustAnchorExtraDataAddress( address genericExtraDataAddress );
    /// @dev Event for setting up a unique Trust Anchor's ExtraData fields.
    event EVT_setUniqueTrustAnchorExtraDataAddress( address trustAnchorAddress, address uniqueExtraDataAddress );
    /// @dev Event for onboarding a Trust Anchor.
    event EVT_onboardTrustAnchor( address trustAnchorAddress );
    /// @dev Event for verifying a Trust Anchor.
    event EVT_verifyTrustAnchor( address trustAnchorAddress );

//    event EVT_isPrimeRevocationManager(address shyftConduitAddress, address _address);

    /// @dev Enum for Trust Anchor system permission status.
    enum TrustAnchorAccess { Unknown, Onboarded, Verified, Demoted }

    //trust anchors
    /// @dev Mapping for Trust Anchor public addresses the enum of TrustAnchorAccess to keep track of system permission status.
    mapping (address => TrustAnchorAccess) trustAnchorAccess;
    /// @dev Mapping for Trust Anchor public addresses to Indexes.
    mapping (address => uint32) trustAnchorIndexes;
    /// @dev Mapping for Trust Anchor Indexes to public addresses.
    mapping (uint32 => address) trustAnchorIndexToAddressMap;

    /// @dev Number of Trust Anchors in this Trust Anchor Manager.
    uint32 numTrustAnchors;

    //jurisdictions
    /// @dev Mapping for Trust Anchor public addresses to Jurisdictions.
    mapping (address => uint16) jurisdictions;
    /// @dev Mapping for Trust Anchor public addresses to Jurisdictions requiring permissions to change.
    mapping (address => uint16) jurisdictionsRequiringChange;

    //extraData
    /// @dev Mapping for a generic system-wide extra data smart contract (ex: ENS-like registration for web2.0 ip addresses for data retrieval).
    address genericExtraDataAddress = address(0);
    /// @dev Mapping for Trust Anchor public addresses to smart contracts that define the Trust Anchor's extra data (Trust-Anchor specific data, like regulator-specific requirements).
    mapping (address => address) uniqueExtraDataAddress;

    /// @dev Base constant for no Jurisdiction set, the default for all Verified Trust Anchors.
    uint16 constant NoJurisdiction = 2 ** 16 - 1;

    /// @dev The Shyft Conduit public address, set post-initialization using multisignature.
    address public shyftConduitAddress = address(0);

    /// @dev Initializes Trust Anchor Manager with the owner set to the calling public address.
    constructor() {
        owner = msg.sender;
    }

    // ** conduit management ** //

    /// @param _shyftConduitAddress The smart contract address for the Shyft Conduit that should be linked.
    /// @dev | Links Shyft Conduit to this contract's function flow.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0: not administrator
    ///    | 1: could not set conduit address
    ///    | 2: set conduit address

    function setupShyftConduit(address _shyftConduitAddress) public returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            //conduit is a stable address anyhow
            if (shyftConduitAddress == address(0) && _shyftConduitAddress != address(0)) {
                shyftConduitAddress = _shyftConduitAddress;

                //set conduit address
                return 2;
            } else {
                //could not set conduit address
                return 1;
            }
        } else {
            //not administrator
            return 0;
        }
    }

    // ** trust anchor primary management ** //

    /// @param _trustAnchorAddress The smart contract address for the trust anchor that should be onboarded.
    /// @dev | Onboards a Trust Anchor, using a public address.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = trust anchor not unknown status, or is zero address
    ///    | 3 = trust anchor onboarded

    function onboardTrustAnchor(address _trustAnchorAddress) public returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("onboardTrustAnchor", _trustAnchorAddress));

            uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);
            if (numConfirmedPermissions >= adminConfirmationThreshold) {
                if (_trustAnchorAddress != address(0) && trustAnchorAccess[_trustAnchorAddress] == TrustAnchorAccess.Unknown) {
                    trustAnchorAccess[_trustAnchorAddress] = TrustAnchorAccess.Onboarded;

                    adminResetPermissionsForMultisignKey(keyKeccak);

                    //trust anchor onboarded
                    emit EVT_onboardTrustAnchor(_trustAnchorAddress);
                    return 3;
                } else {
                    adminResetPermissionsForMultisignKey(keyKeccak);

                    //trust anchor not unknown status, or is zero address
                    return 2;
                }
            } else {
                //not enough administrators have permissioned change
                return 1;

            }
        } else {
            //not an administrator
            return 0;
        }
    }

    // ** trust anchor primary management ** //

    /// @param _trustAnchorAddress The smart contract address for the trust anchor that should be verified.
    /// @dev | Verifies Trust Anchor, using a public address. Onboarded and Verified are seperate processes to keep track of Trust Anchors that may be temporarily Demoted.
    ///      | Also useful as the pattern 1. Onboarded -> 2. Trust Anchor sets their own "extraData" which contains details that are required (for regulations etc.) -> 3. Administrators vote to have Trust Anchors fully verified having examined the data in the extraData smart contract pointer.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = trust anchor not onboarded status
    ///    | 3 = trust anchor verified

    function verifyTrustAnchor(address _trustAnchorAddress) public returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("verifyTrustAnchor", _trustAnchorAddress));

            uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);
            if (numConfirmedPermissions >= adminConfirmationThreshold) {
                if (trustAnchorAccess[_trustAnchorAddress] == TrustAnchorAccess.Onboarded ||
                trustAnchorAccess[_trustAnchorAddress] == TrustAnchorAccess.Demoted) {
                    trustAnchorAccess[_trustAnchorAddress] = TrustAnchorAccess.Verified;
                    jurisdictions[_trustAnchorAddress] = NoJurisdiction;

                    if (trustAnchorIndexes[_trustAnchorAddress] == 0) {
                        //NOTE that the first index of trustAnchorIndexes is now empty.
                        numTrustAnchors++;
                        trustAnchorIndexes[_trustAnchorAddress] = numTrustAnchors;
                        trustAnchorIndexToAddressMap[numTrustAnchors] = _trustAnchorAddress;
                    }

                    adminResetPermissionsForMultisignKey(keyKeccak);

                    //trust anchor verified
                    emit EVT_verifyTrustAnchor(_trustAnchorAddress);
                    return 3;
                } else {
                    adminResetPermissionsForMultisignKey(keyKeccak);

                    //trust anchor not unknown state
                    return 2;
                }
            } else {
                //not enough administrators have permissioned change
                return 1;
            }
        } else {
            //not an administrator
            return 0;
        }
    }

    /// @param _trustAnchorAddress The smart contract address for the trust anchor that should be demoted.
    /// @dev | Demotes Trust Anchor, using a public address.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = trust anchor not verified status
    ///    | 3 = trust anchor successfully demoted

    function demoteTrustAnchor(address _trustAnchorAddress) public returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("demoteTrustAnchor", _trustAnchorAddress));

            uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);
            if (numConfirmedPermissions >= adminConfirmationThreshold) {
                if (trustAnchorAccess[_trustAnchorAddress] == TrustAnchorAccess.Verified) {
                    trustAnchorAccess[_trustAnchorAddress] = TrustAnchorAccess.Demoted;

                    //trust anchor successfully demoted
                    return 3;
                } else {
                    //trust anchor not verified state
                    return 2;
                }

            } else {
                //not enough administrators have permissioned change
                return 1;

            }
        } else {
            //not an administrator
            return 0;
        }
    }

    /// @param _trustAnchorAddress The smart contract address that is checked for Verified status.
    /// @dev Checks whether a Trust Anchor is verified, using a public address.
    /// @return result
    ///    | false = not verified
    ///    | true = is verified

    function isTrustAnchorVerified(address _trustAnchorAddress) public view override returns (bool result) {
        if (trustAnchorAccess[_trustAnchorAddress] == TrustAnchorAccess.Verified) {
            //is verified
            return true;
        } else {
            //not verified
            return false;
        }
    }

    /// @param _trustAnchorAddress The smart contract address that is checked for Verified status.
    /// @dev Checks whether a Trust Anchor has ever been verified (could currently be Demoted, but must have been Verified at least once in the past), using a public address.
    /// @return result
    ///    | false = has not been verified
    ///    | true = has been verified

    function hasTrustAnchorBeenVerified(address _trustAnchorAddress) public view override returns (bool result) {
        if (trustAnchorAccess[_trustAnchorAddress] == TrustAnchorAccess.Verified || trustAnchorAccess[_trustAnchorAddress] == TrustAnchorAccess.Demoted) {
            //has been verified
            return true;
        } else {
            //has not been verified
            return false;
        }
    }

    /// @param _trustAnchorAddress The smart contract address that is checked for the Index for this Trust Anchor.
    /// @dev | Gets the Index of a Trust Anchor, using a public address. This Index is a simple integer value
    ///      | that relates to the order of Trust Anchors that have been onboarded into this contract.
    /// @return result
    ///    | 0 -> 2^32-1 = index of trust anchor

    function getTrustAnchorIndex(address _trustAnchorAddress) external view override returns (uint32 result) {
        return trustAnchorIndexes[_trustAnchorAddress];
    }

    /// @param _trustAnchorIndex The Index that is checked for the public address for this Trust Anchor.
    /// @dev Gets the public address of a Trust Anchor, using an Index. This public address.
    /// @return result the public address of this trust anchor

    function getTrustAnchorAddress(uint32 _trustAnchorIndex) external view override returns (address result) {
        return trustAnchorIndexToAddressMap[_trustAnchorIndex];
    }


    // ** jurisdictions management ** //

    /// @param _newJurisdiction Jurisdiction to be set for a specific Trust Anchor.
    /// @dev | Sets up a Jurisdiction for a Trust Anchor, contract is called by the Trust Anchors themselves.
    ///      | Requires multisignature approval for changing jurisdiction.
    /// @return result
    ///    | 0 = not verified
    ///    | 1 = jurisdiction already set, now waiting for approval
    ///    | 2 = trust anchor jurisdiction has been set up first time

    function setupTrustAnchorJurisdiction(uint16 _newJurisdiction) public returns (uint8 result) {
        if (isTrustAnchorVerified(msg.sender) == true) {
            if (jurisdictions[msg.sender] == NoJurisdiction) {
                jurisdictions[msg.sender] = _newJurisdiction;

                //trust anchor jurisdiction has been set up first time
                return 2;
            } else {
                jurisdictionsRequiringChange[msg.sender] = _newJurisdiction;
                emit EVT_JurisdictionDoesRequireChange(msg.sender);

                //jurisdiction already set, now waiting for approval
                return 1;
            }
        } else {
            //not verified
            return 0;
        }
    }

    /// @param _trustAnchorAddress Trust Anchor public address to confirm a change of Jurisdiction.
    /// @dev | Approves the change to a Jurisdiction for a Trust Anchor.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not administrator
    ///    | 1 = no trust anchor found
    ///    | 2 = no jurisdiction change necessary
    ///    | 3 = jurisdiction change occurred

    function approveChange(address _trustAnchorAddress) public returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            //@note:@here:@next:@todo: set up a multi-signature requirement for approval?
            if (jurisdictions[_trustAnchorAddress] != 0) {
                if (jurisdictionsRequiringChange[_trustAnchorAddress] != 0 &&
                    jurisdictionsRequiringChange[_trustAnchorAddress] != jurisdictions[_trustAnchorAddress]) {
                    jurisdictions[_trustAnchorAddress] = jurisdictionsRequiringChange[_trustAnchorAddress];
                    //@note: @here: this would cause this call to fail with a "Error: base fee exceeds gas limit" error.
                    //not sure why.
                    //delete jurisdictionsRequiringChange[_trustAnchor];

                    //jurisdiction change occurred
                    return 3;
                } else {
                    //no jurisdiction change necessary
                    return 2;
                }
            } else {
                //no trust anchor found
                return 1;
            }
        } else {
            //not administrator
            return 0;
        }
    }

    /// @param _trustAnchorAddress Trust Anchor public address to get the Jurisdiction of.
    /// @dev Gets the Jurisdiction for a Trust Anchor. Any invalid Trust Ancors will return 0.
    /// @return result
    ///    | 0 -> 2 ** 16 - 2 = jurisdiction found
    ///    | 2 ** 16 - 1 = no jurisdiction found

    function getTrustAnchorJurisdiction(address _trustAnchorAddress) public override returns (uint16 result) {
        //jurisdiction found, or 2 ** 31 - 1 is no jurisdiction set

        emit EVT_GettingJurisdictionFromTrustAnchor(jurisdictions[_trustAnchorAddress], _trustAnchorAddress);

        return jurisdictions[_trustAnchorAddress];
    }


    // ** generic & unique extra data management ** //

    /// @param _genericExtraDataAddress The smart contract address that contains additional functionality/variables that are useful to the Shyft ecosystem. All Trust Anchors set up in this contract would find these useful.
    /// @dev | Sets the generic ExtraData that would be useful to all of the Trust Anchors associated to this smart contract, mediated by the administrators of this smart contract.
    ///      | An example of this "ExtraData" would be the web2.0 ip addresses that correspond to data retrieval of end user's PII according to the consent rules.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = invalid extraData address
    ///    | 3 = set generic extraData address

    function setGenericTrustAnchorExtraDataAddress(address _genericExtraDataAddress) public returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("setGenericTrustAnchorExtraDataContract", _genericExtraDataAddress));

            uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);
            if (numConfirmedPermissions >= adminConfirmationThreshold) {
                if (_genericExtraDataAddress != address(0)) {
                    genericExtraDataAddress = _genericExtraDataAddress;

                    emit EVT_setGenericTrustAnchorExtraDataAddress(genericExtraDataAddress);

                    adminResetPermissionsForMultisignKey(keyKeccak);
                    //set generic extraData
                    return 3;
                } else {
                    //invalid extraData address
                    return 2;
                }
            } else {
                //not enough administrators have permissioned change
                return 1;

            }
        } else {
            //not an administrator
            return 0;
        }
    }

    /// @dev | Gets the generic ExtraData that would be useful to all of the Trust Anchors associated to this smart contract, mediated by the administrators of this smart contract.
    ///      | An example of this "ExtraData" would be the web2.0 ip addresses that correspond to data retrieval of end user's PII according to the consent rules.
    /// @return result returns the generic extraData smart contract address

    function getGenericTrustAnchorExtraDataAddress() public view override returns (address result) {
        // returns the generic extraData smart contract address
        return genericExtraDataAddress;
    }

    /// @param _uniqueExtraDataAddress The smart contract address that contains additional functionality/variables that are useful to the Shyft ecosystem, but are not included in the generic ExtraData.
    /// @dev | Sets the "ExtraData" smart contract address for a Trust Anchor. This points to another smart contract which can handle variable data that can evolve over time without impacting this contract, this function call is mediated by the Trust Anchor themselves.
    ///      | Some examples of this "ExtraData" would be any local regulatory requirements (web 2.0 links, embedded files, signatures from regulators, etc) that would need to be fulfilled, other examples would include smart contract addresses that link to blockchain systems that are not Shyft but are compliance-related.
    /// @return result
    ///    | 0 = sender is not a valid trust anchor
    ///    | 1 = set trust anchor specific extraData

    function setUniqueTrustAnchorExtraDataAddress(address _uniqueExtraDataAddress) public returns (uint8 result) {
        if (trustAnchorAccess[msg.sender] != TrustAnchorAccess.Unknown) {
            uniqueExtraDataAddress[msg.sender] = _uniqueExtraDataAddress;

            emit EVT_setUniqueTrustAnchorExtraDataAddress(msg.sender, uniqueExtraDataAddress[msg.sender]);

            //set trust anchor specific extraData
            return 1;
        } else {
            //sender is not a valid trust anchor
            return 0;
        }
    }

    /// @param _trustAnchorAddress Trust Anchor public address to get the Jurisdiction of.
    /// @dev | Gets the "ExtraData" smart contract address for a Trust Anchor. This points to another smart contract which can handle variable data that can evolve over time without impacting this contract, mediated by the Trust Anchor themselves.
    ///      | Some examples of this "ExtraData" would be any local regulatory requirements (web 2.0 links, embedded files, signatures from regulators, etc) that would need to be fulfilled, other examples would include smart contract addresses that link to blockchain systems that are not Shyft but are compliance-related.
    /// @return result the smart contract address associated to a specific trust anchor that will contain any other data relevant to its functioning

    function getUniqueTrustAnchorExtraDataAddress(address _trustAnchorAddress) public view override returns (address result) {
        //returns the smart contract address associated to a specific trust anchor that will contain any other data relevant to its functioning
        return uniqueExtraDataAddress[_trustAnchorAddress];
    }

    // ** prime revocation manager address retrieval ** //

    /// @param _primeRevocationManagerAddress A public address to check for equality to the Prime Revocation Address.
    /// @dev | The Prime Revocation Address is one which is set in the internal Shyft Core architecture.
    ///      | This Prime Revocation Address is responsible & allowed to perform revocations for edge-cases, such as a malicious Trust Anchor that has been forcibly removed from the system without it clearing its attestations beforehand.
    /// @return result
    ///    | true = is the prime revocation address
    ///    | false = is not the prime revocation address

    function isPrimeRevocationManager(address _primeRevocationManagerAddress) public override returns (bool result) {
        IShyftConduit shyftConduit = IShyftConduit(shyftConduitAddress);

//        emit EVT_isPrimeRevocationManager(shyftConduitAddress, _primeRevocationManagerAddress);

        if (shyftConduitAddress != address(0) && shyftConduit.isPrimeRevocationManager(_primeRevocationManagerAddress) == true) {
            return true;
        } else {
            return false;
        }
    }
}

