pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "./Administrable.sol";
import "./Interfaces/ITrustAnchorManager.sol";
import "./Interfaces/ICuratedBinding.sol";

contract CuratedBinding is Administrable, ICuratedBinding {

    string name;
    mapping(bytes32 => bytes32) merkleRoots;
    mapping(bytes32 => uint256) channelForms;

    mapping(address => bytes32[]) trustAnchorMerkleRootsArray;

    //reverse mapping to get the trust anchor that proposed the a merkle header.
    mapping(bytes32 => address) headerKeccakToTrustAnchorSourceMapping;

    address trustAnchorManagerAddress = address(0);

    constructor() {
        owner = msg.sender;
    }

    //returns:
    // false = not owner
    // true = is owner

    function isOwner() internal view returns (bool result) {
        if (msg.sender == owner) {
            //is owner
            return true;
        } else {
            //not owner
            return false;
        }
    }

    //returns:
    // 0 = trust anchor is not verified
    // 1 = set header correctly

    function setHeader(bytes32 _merkleRoot, uint256 _channelForm) public override returns (uint8 result) {
        ITrustAnchorManager trustAnchorManager = ITrustAnchorManager(trustAnchorManagerAddress);

        if (trustAnchorManager.isTrustAnchorVerified(msg.sender)) {
            bytes32 headerKeccak = keccak256(abi.encodePacked(msg.sender, _merkleRoot));

            merkleRoots[headerKeccak] = _merkleRoot;
            channelForms[headerKeccak] = _channelForm;

            trustAnchorMerkleRootsArray[msg.sender].push(_merkleRoot);

            headerKeccakToTrustAnchorSourceMapping[headerKeccak] = msg.sender;

            //set header correctly
            return 1;
        } else {
            //trust anchor is not verified
            return 0;
        }
    }

    //returns:
    // n = merkle header for hash
    function getMerkleRootFromHeader(bytes32 _headerKeccak) public override returns (bytes32 result) {
        return merkleRoots[_headerKeccak];
    }

    //returns:
    // n = channel form for hash
    function getChannelFormFromHeader(bytes32 _headerKeccak) public override returns (uint256 result) {
        return channelForms[_headerKeccak];
    }

    function getTrustAnchorFromHeaderKeccak(bytes32 _headerKeccak) public override returns (address result) {
        return headerKeccakToTrustAnchorSourceMapping[_headerKeccak];
    }
}
