pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

interface IShyftSafe {
    function isBridge(address _addressToCheck) external view returns (bool result);
    function setBridge(address _addr) external;

    function getNumberOfHolds(address _address) external view returns (uint256 result);
    function getAmountForHold(address _address, uint256 _holdIndex) external view returns (uint256 result);
    function getBip32XTypeForHold(address _address, uint256 _holdIndex) external view returns (uint256 result);
    function getExpiryTimeForHold(address _address, uint256 _holdIndex) external view returns (uint256 result);

    function getIsTransferPermissionedAddressForHold(address _address, address _permissionedAddress, uint256 _holdIndex) external view returns (uint8 result);
    function getIsTimelockedForHold(address _address, uint256 _holdIndex) external view returns (uint8 result);
    function getIsActiveForHold(address _address, uint256 _holdIndex) external view returns (uint8 result);

    function setSafeHoldDestructible(uint256 _holdIndex) external returns (uint8 result);
    function burnSafeHold(uint256 _holdIndex) external returns (uint8 result);
    function mintSafeHold(address _address, uint256 _amount, uint256 _bip32X_type, uint64 _safeExpiryTime) external returns (uint8 result, uint256 holdIndex);

    function transferFromSafeHold(address _fromAddress, uint256 _holdIndex) external returns (uint8 result, uint256 holdIndex);
}
