pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "../Interfaces/IShyftBlockRewardAdministration.sol";
import "../Interfaces/IOwned.sol";
import "../Interfaces/IBaseOwnedSet.sol";
import "../Interfaces/IRelaySet.sol";
import "../Interfaces/IRelayedOwnedSet.sol";
import "../Administrable.sol";

///@dev This contract mediates the voting process for new members into the validator set.

contract ShyftProtocolValidatorAdministration is Administrable {
    event EVT_AddValidator(address _validator);
    event EVT_RemoveValidator(address _validator);
    event EVT_SetNewOwner(address _ownedContractAddress, address _newOwnerAddress);

    event EVT_SetVotingRelaySetContractAddress(address _votingRelaySetContractAddress, address _votingTargetRelayedSetContractAddress);
    event EVT_SetBlockRewardContractAddress(address _blockRewardContractAddress);

    event EVT_RelayedOwnedSet_SetRelay(address _relaySet);
    event EVT_RelayedOwnedSet_SetRecentBlocks(uint256 _recentBlocks);

    event EVT_RelaySet_SetRelayed(address _relayedSet);

    address public votingRelaySetContractAddress = address(0);
    address public votingTargetRelayedSetContractAddress = address(0);
    address public blockRewardContractAddress = address(0);

    constructor() {
        //@note: override Administrable's defaults NofM multi-sig administrators confirmation thresholds.
        adminConfirmationThreshold = 1;

        // set owner to message sender.
        owner = msg.sender;
    }

    // the administrator in this case could be a delegate previously set within the block reward context.
    function isAdministrator(address _validatorAddress) public view override returns (bool result) {
        IBaseOwnedSet baseOwnedSet = IBaseOwnedSet(votingTargetRelayedSetContractAddress);
        return baseOwnedSet.getIsValidator(_validatorAddress);
    }

    function getNumValidators() public view returns (uint256 result) {
        IBaseOwnedSet baseOwnedSet = IBaseOwnedSet(votingTargetRelayedSetContractAddress);
        return baseOwnedSet.getNumValidators();
    }

    // ------------------------------------------------------
    // System linkage/setup

    function setVotingRelaySetContractAddress(address _votingRelaySetContractAddress) public returns (uint8 result) {
        bytes32 keyKeccak = keccak256(abi.encodePacked("setVotingRelaySetContractAddress", _votingRelaySetContractAddress));

        bool permittedToModify;
        bool multiSigMode;

        if (msg.sender == owner && numAdministrators == 0) {
            permittedToModify = true;
        } else if (isAdministrator(msg.sender)) {

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify != true) {
                //added vote to add validator
                return 1;
            } else {
                multiSigMode = true;
            }
        }

        if (permittedToModify == true) {
            votingRelaySetContractAddress = _votingRelaySetContractAddress;

            if (multiSigMode == true) {
                adminResetPermissionsForMultisignKey(keyKeccak);
            }

            updateVotingRelayedSetContractAddress(_votingRelaySetContractAddress);

            emit EVT_SetVotingRelaySetContractAddress(votingRelaySetContractAddress, votingTargetRelayedSetContractAddress);

            //set voting relay set & target relayed set addresses
            return 2;
        } else {
            //not permitted to modify voting relay set address
            return 0;
        }
    }

    function updateVotingRelayedSetContractAddress(address _votingRelaySetAddress) internal returns (uint8 result) {
        IRelaySet relaySet = IRelaySet(_votingRelaySetAddress);

        votingTargetRelayedSetContractAddress = relaySet.getRelayed();

        if (votingTargetRelayedSetContractAddress != address(0)) {
            IBaseOwnedSet baseOwnedSet = IBaseOwnedSet(votingTargetRelayedSetContractAddress);

            address[] memory curValidators = baseOwnedSet.getValidators();

            // add the existing administrators into the set. perform these actions manually (referencing Administrable),
            // making sure to increment the internal numAdministrators.
            for (uint i = 0; i < curValidators.length; i++) {
                // check for administrator already in the set, if so don't add again.
                if (administrators[curValidators[i]] == AdministratorAccess.Demoted ||
                    administrators[curValidators[i]] == AdministratorAccess.Unknown) {

                    administrators[curValidators[i]] = AdministratorAccess.Promoted;
                    numAdministrators++;

                    emit EVT_AdminPromoted(curValidators[i]);
                }
            }

            // at this point, the "RelayOwnedSet" contract needs to "setOwner" (from BaseOwnedSet->OwnedSet)
            // to this contract's deployed address. At that point, this contract can add/remove validators,
            // or update the ownership of the "RelayOwnedSet" contract address.

            //set updated voting relayed set contract address and updated validators
            return 1;
        } else {

            //set voting relayed set contract, but the address was zero
            return 0;
        }
    }

    function setBlockRewardContractAddress(address _blockRewardContractAddress) public returns (uint8 result) {
        bytes32 keyKeccak = keccak256(abi.encodePacked("setBlockRewardContractAddress", _blockRewardContractAddress));

        bool permittedToModify;
        bool multiSigMode;

        if (msg.sender == owner && numAdministrators == 0) {
            permittedToModify = true;
        } else if (isAdministrator(msg.sender)) {

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify != true) {
                //added vote to add validator
                return 1;
            } else {
                multiSigMode = true;
            }
        }

        if (permittedToModify == true) {
            blockRewardContractAddress = _blockRewardContractAddress;

            if (multiSigMode == true) {
                adminResetPermissionsForMultisignKey(keyKeccak);
            }

            emit EVT_SetBlockRewardContractAddress(blockRewardContractAddress);

            //successfully set block reward address
            return 2;
        } else {
            //not permitted to modify block reward address
            return 0;
        }
    }

    /// @param _keyKeccak A hash to reference as the key/value vote subject.
    /// @dev Applies an administrator's vote & updates the votes for a specific multisignature key/value object.
    /// @notice Uses composition features (based on current validator set) so data structure may be orphaned when promotion/demotion events occur.
    /// @notice Override's Administrator's base implementation
    /// @return result
    ///    | 0 = not administrator
    ///    | 1 to numAdministrators = #confirmations

    function adminApplyAndGetPermissionsForMultisignKey(bytes32 _keyKeccak) internal override returns (uint256 result) {
        IShyftBlockRewardAdministration blockRewardAdmin = IShyftBlockRewardAdministration(blockRewardContractAddress);

        bytes32 composedKeyKeccak = composeKeyKeccak(_keyKeccak);

        address delegatedBenefactorAddress = blockRewardAdmin.getDelegatedBenefactorAddress(msg.sender);

        // if not a delegate, use msg.sender
        if (delegatedBenefactorAddress == address(0))
            delegatedBenefactorAddress = msg.sender;

        if (administratorMultisignPermissionedKeys[composedKeyKeccak].numConfirmations < getNumValidators() &&
            (administratorMultisignPermissionedKeys[composedKeyKeccak].administratorSignatures[delegatedBenefactorAddress] == KeyPermissionAccess.Unknown ||
            administratorMultisignPermissionedKeys[composedKeyKeccak].administratorSignatures[delegatedBenefactorAddress] == KeyPermissionAccess.Reset)) {
            // increase permission level of the key, apply signature.
            administratorMultisignPermissionedKeys[composedKeyKeccak].numConfirmations++;
            administratorMultisignPermissionedKeys[composedKeyKeccak].administratorSignatures[delegatedBenefactorAddress] = KeyPermissionAccess.Signed;

            administratorMultisignPermissionedKeys[composedKeyKeccak].administrators.push(delegatedBenefactorAddress);
        }

        //(one to the numAdministrators) #confirmations
        return administratorMultisignPermissionedKeys[composedKeyKeccak].numConfirmations;
    }

    // override adding/removing administrators, as the underlying validator set obtained from
    // setVotingRelaySetContractAddress() is considered the source of truth for the "administrators" in this
    // contract's management context.

    function setPrimaryAdministrator(address _newAdministratorAddress) public override returns (uint8 result) {        // override always halts additions of administrators
        //override always halts setting of the primary administrator
        return 0;
    }

    function promoteAdministrator(address _newAdministratorAddress) public override returns (uint8 result) {
        //override always halts additions of administrators
        return 0;
    }

    function revokeAdministrator(address _revokeAdministratorAddress) public override returns (uint8 result) {
        //override always halts revocation of administrators
        return 0;
    }

    // ------------------------------------------------------
    // "BaseOwnedSet" multi-sig function relays

    function addValidator(address _validatorAddress) public returns (uint8 result) {
        uint8 promoteResult = super.promoteAdministrator(_validatorAddress);
        if (promoteResult == 2) {
            IBaseOwnedSet baseOwnedSet = IBaseOwnedSet(votingTargetRelayedSetContractAddress);

            baseOwnedSet.addValidator(_validatorAddress);

            emit EVT_AddValidator(_validatorAddress);

            //successfully added validator
            return promoteResult;
        } else {
            //either not validator or already validator
          return promoteResult;
        }
    }

    function removeValidator(address _validatorAddress) public returns (uint8 result) {
        uint8 removeResult = super.revokeAdministrator(_validatorAddress);
        if (removeResult == 3) {
            IBaseOwnedSet baseOwnedSet = IBaseOwnedSet(votingTargetRelayedSetContractAddress);

            baseOwnedSet.removeValidator(_validatorAddress);

            emit EVT_RemoveValidator(_validatorAddress);

            //successfully removed validator
            return removeResult;
        } else {
            //either not validator or already validator.
            return removeResult;
        }
    }

    // ------------------------------------------------------
    // "IOwner" multi-sig function relays

    // these functions set up new Owners for the [voting relay], and [voting target set] contracts.
    // this can only be done *after* calling setOwner([this contract's address]) from the contracts.

    function votingRelaySet_setOwner(address _newOwnerAddress) public returns (uint8 result) {
        return delegateNewOwner(votingRelaySetContractAddress, _newOwnerAddress);
    }

    function votingTargetRelayedSet_setOwner(address _newOwnerAddress) public returns (uint8 result) {
        return delegateNewOwner(votingTargetRelayedSetContractAddress, _newOwnerAddress);
    }

    function delegateNewOwner(address _ownedContractAddress, address _newOwnerAddress) internal returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("delegateNewOwner", _ownedContractAddress, _newOwnerAddress));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                IOwned owned = IOwned(_ownedContractAddress);

                owned.setOwner(_newOwnerAddress);

                adminResetPermissionsForMultisignKey(keyKeccak);

                emit EVT_SetNewOwner(_ownedContractAddress, _newOwnerAddress);

                //added validator
                return 2;
            } else {
                //added vote to add validator
                return 1;
            }
        } else {
            //not validator
            return 0;
        }
    }

    // ------------------------------------------------------
    // "IRelayedOwnedSet" and "IBaseOwnedSet" multi-sig function relays

    function votingTargetRelayedSet_setRelay(address _relaySet) external returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("votingTargetRelayedSet_setRelay", _relaySet));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                IRelayedOwnedSet relayedOwnedSet = IRelayedOwnedSet(votingTargetRelayedSetContractAddress);

                relayedOwnedSet.setRelay(_relaySet);

                adminResetPermissionsForMultisignKey(keyKeccak);

                emit EVT_RelayedOwnedSet_SetRelay(_relaySet);

                //set relay set
                return 2;
            } else {
                //added vote to add validator
                return 1;
            }
        } else {
            //not validator
            return 0;
        }
    }

    function votingTargetRelayedSet_setRecentBlocks(uint256 _recentBlocks) external returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("votingTargetRelayedSet_setRecentBlocks", _recentBlocks));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                IBaseOwnedSet baseOwnedSet = IBaseOwnedSet(votingTargetRelayedSetContractAddress);

                baseOwnedSet.setRecentBlocks(_recentBlocks);

                adminResetPermissionsForMultisignKey(keyKeccak);

                emit EVT_RelayedOwnedSet_SetRecentBlocks(_recentBlocks);

                //set recent blocks
                return 2;
            } else {
                //added vote to add validator
                return 1;
            }
        } else {
            //not validator
            return 0;
        }
    }

    // ------------------------------------------------------
    // "IRelaySet" multi-sig function relays

    function votingRelaySet_setRelayed(address _relayedSet) external returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("votingRelaySet_setRelayed", _relayedSet));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                IRelaySet relaySet = IRelaySet(votingRelaySetContractAddress);

                relaySet.setRelayed(_relayedSet);

                adminResetPermissionsForMultisignKey(keyKeccak);

                emit EVT_RelaySet_SetRelayed(_relayedSet);

                //set relayed set
                return 2;
            } else {
                //added vote to add validator
                return 1;
            }
        } else {
            //not validator
            return 0;
        }
    }
}

