//@note:@todo:@next:@critical: master key management.. I'm not sure this strategy works given that it's all
// public functions. This may have to be a separately linked contract
library ShyftSafeManagement {
    struct safeManagement {
        //master key management.
        mapping(address => address) userMasterKey;
        mapping(address => address) keyManagementContractAddress;

        //backup planner management.
        mapping(address => address) userBPChoice;
        mapping(address => address) userWithdrawalRulesContractAddress;

        //'skirts (bridged blockchains) management.
        //@note:@todo:@critical: this could be extended into a data-requirement for the bridge, with tests et al. from an origin/destination chain pair.this
        // ie. as soon as the bridge transit is completed, set up the account on that side by referencing a proof of this data block. if wanting to do a proof
        // on multiple pieces of data.. what's the response.. auto-update a single-value hash of all of the parameters?
        mapping(address => mapping(uint256 => uint256)) userSkirtsSpendingLimit;
    }

    //@note: sets master key for account. can only be done once!

    //returns:
    // 0 = master key already set
    // 1 = successfully set master key

    function setMasterKey(safeManagement storage _management, address _masterKey) public returns (uint8 result) {
        if (_management.userMasterKey[msg.sender] == address(0)) {
            //@note:@todo:@next:@wallet
            _management.userMasterKey[msg.sender] = _masterKey;

            //successfully set master key
            return 1;
        } else {

            //master key already set
            return 0;
        }
    }

    //@note:@todo:@future: complete the following functions
    /**************** Shyft Safe Master Key ****************/

    //@note: this can be used for a variety of (non-PII) user settings they'd like to persist.
    //results:
    // 0 = user does not own account
    // 1 = successfully set profile management

    function setupProfileManagement(safeManagement storage _management, address _account, address _profileManagementContractAddress) public returns (uint8 result) {
        if (_management.userMasterKey[_account] == msg.sender) {
            _management.keyManagementContractAddress[_account] = _profileManagementContractAddress;

            //successfully set profile management
            return 1;
        } else {
            //user does not own account
            return 0;
        }
    }

    //@note: sets a user's backup planner choices.
    // _backupPlannerAddress is the backup planner of choice, this smart contract can be multisignature etc.
    // used for withdrawal
    // _withdrawalRulesContractAddress is the rules that need to pass to be withdrawn. default implementation of
    // this smart contract should be to only allowed to start from the preservation's rules (48 hrs block on transfer,
    // an action revokeable by the user's master key).

    //results:
    // 0 = user does not own account
    // 1 = successfully set BP choice

    function setBPChoice(safeManagement storage _management, address _account, address _backupPlannerAddress, address _withdrawalRulesContractAddress) public returns (uint8 result) {
        if (_management.userMasterKey[_account] == msg.sender) {
            //@note:@todo:@next:@wallet
            _management.userBPChoice[_account] = _backupPlannerAddress;
            _management.userWithdrawalRulesContractAddress[_account] = _withdrawalRulesContractAddress;

            //successfully set BP choice
            return 1;
        } else {
            //user does not own account
            return 0;
        }
    }

    //@note: places all assets into the Shyft Safe preservation.

    //results:
    // 0 = user does not own account
    // 1 = successfully chilled account

    function chillAccount(safeManagement storage _management, address _account) public view returns (uint8 result) {
        if (_management.userMasterKey[_account] == msg.sender) {
            //@note:@todo:@next:@wallet
            // loop through linked list of holds. send them to the pool.
            //

            //successfully chilled account
            return 1;
        } else {
            //user does not own account
            return 0;
        }
    }

    //@note: sets the spending limits on the various 'skirts that Shyft bridges to.

    //results:
    // 0 = user does not own account
    // 1 = successfully set spending limit

    function setSpendingLimitOnSkirts(safeManagement storage _management, address _account, uint32 _bip32XType, uint256 _amount) public returns (uint8 result) {
        if (_management.userMasterKey[_account] == msg.sender) {
            //@note:@todo:@next:@wallet
            // set spending limit
            _management.userSkirtsSpendingLimit[_account][_bip32XType] = _amount;

            //successfully set spending limit
            return 1;
        } else {
            //user does not own account
            return 0;
        }
    }
}

