//pragma experimental ABIEncoderV2;
pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "./Libraries/ABDKMath64x64.sol";
import "./Libraries/SafeMath.sol";

import "./Interfaces/ITrustAnchorManager.sol";
import "./Interfaces/ITrustAnchorStorage.sol";

/// @dev
/// | A reference contract related to the Trust Anchor Storage's availability address component within the attestations, which is an ethereum-type address corresponding to the smart contract that manages the various economic & regulatory conditions of data transfer from a Trust Anchor to a data retriever.
/// | Within the Trust Anchor Storage's attestation framework, the availability address is generally encrypted via symmetric AES to prevent abstracted PII from being leaked on the blockchain directly. Once revealed however, this contract and all of its storage variables are trivially linked to the attestation that sets this.
/// |
/// | those applying escrow need to be trust anchors
/// | workflow looks like:
/// |
/// | 1) attestation is set
/// |
/// | 2a) managed (user gives pii-holding trust anchor permission to deal with data transfer, select targets, etc.) sets a data retrieval target (must be valid ta)
/// |
/// | 2b) unmanaged (user gives permission/consent to pii-holding trust anchor to transfer data to another trust anchor manually) sets a data retrieval target (must be valid ta)
/// |
/// | 3) destination trust anchor puts escrow funds (SHFT tokens) into this smart contract via the "applyEscrow" function. this specific smart contract *only* uses SHFT tokens to transfer, this can be extended by either re-linking an upgraded version of this smart contract on the trust anchor manager or extending a SHFT<->Bancor (or equivalent) token bridge.
/// |
/// | 4a) source trust anchor, watching this contract's events, sees that this escrow event has occurred. that means it can send its data depending on:
/// |
/// | 4a i) the "contestation oracle", an address that can sign to contest (cancel) an escrow even if the documents have been sent. This "contestation oracle" can be equal to address(0) (a null address, and thus no contestation oracle), or the data retrieval entity itself.
/// |
/// | 4a ii) the "contestation time period", the amount of time after the sender has triggered a "sent the documents" message that the predetermined oracle has to respond and perform the contestation step if they determine the quality/veracity of the documents sent are questionable.
/// |
/// | 4b i) source trust anchor does (an offchain) transfer of user's consented pii data, with the consented data structure available via encrypted bytes in step 2.
/// |
/// | 4b ii) source trust anchor triggers the "setDataSenderDidSendDocuments" function in the Trust Anchor Storage. This indicates the transfer of source documents, and gives the oracle (4a i) the predetermined time (4a ii) to respond to the quality of the sent documents.
/// |
/// | 5) destination trust anchor, having received user's pii data, confirms all of the documents transferred match the consented data structure (at this point both ta's have signed/sent at least one message to the trust anchor manager smart contract, so their public keys are available bilaterally for encrypted communications).
/// |
/// | 6) destination trust anchor triggers the "dataRetrievalEntityValidateAttestation" function in the trust anchor storage smart contract, completing the chain-of-custody relationship.
/// |
/// | 7a) the source trust anchor then triggers the "settleEscrow" function, which confirms that step 6 was performed, and releases the escrow to the source trust anchor.
/// |
/// | 7b) the end-user may trigger the "settleEscrow" function at any point after step 6.
/// |
/// | 8) after 30 days, the data retrieval target may cancel their escrow if the sender has not triggered the "sent the documents" message.


contract TrustAnchorStorage_Availability {
    event EVT_D(uint256 accumulatedAmount);

    /// @dev event for logging of an "escrow" struct. mostly useful for debugging purposes.
//    event EVT_logEscrow(escrow existingEscrow);
    /// @dev event for application of escrow by data retrieval target, including all pertinent details (things like block timestamp can be calculated offline for logging purposes).
    event EVT_appliedEscrow(bytes32 _attestationKeccak, address currentRetrieverAddress, uint256 totalAmount, uint256 trustAnchorDataRetrievalShare);
    /// @dev event for updating of escrow by data retrieval target, including all pertinent details (things like block timestamp can be calculated offline for logging purposes).
    event EVT_updatedEscrow(bytes32 _attestationKeccak, address currentRetrieverAddress, uint256 totalAmount, uint256 trustAnchorDataRetrievalShare);
    /// @dev event for successful contestation of an escrow.
    event EVT_contestEscrow(bytes32 attestationKeccak, address dataRetrievalEntityAddress, address contestationOracleAddress);
    /// @dev event for successful cancellation of an escrow.
    event EVT_cancelEscrow(bytes32 attestationKeccak, address sentFrom, uint256 totalEscrowApplied);
    /// @dev event for settlement for a recipient.
    event EVT_settledRecipient(bytes32 attestationKeccak, address dataTargetAddress, address recipientAddress, uint256 recipientAmount, bool wasCompleted);

    /// @dev using the SafeMath library for any uint256 values.
    using SafeMath for uint256;
    uint256 baseEscrowExpiryTime = 1 * 60 * 60 * 24 * 30; // 30 days expiry time from initial set of escrow.

    /// @dev struct of the escrow components
    struct escrow {
        bool isActive;
        address sentFrom;
        uint256 totalAmount;
        uint256 userAmount;
        uint256 trustAnchorAmount;

        address contestationOracleAddress;
        uint256 contestationTimePeriod;
        bool oracleDidContest;

        uint256 setTimestamp;

        bool hasUserSettled;
        bool hasTrustAnchorSettled;

        bool wasCompleted;
        bool wasCancelled;
    }

    /// @dev mapping of attestation keccak to the retrieval entity to the Escrow itself. escrow = attestationEscrow[_attestationKeccak][dataRetrievalEntityAddress]
    mapping (bytes32 => mapping(address => escrow)) attestationEscrow;

    /// @dev total escrow applied (waiting for settlement based on state machine parameter).
    uint256 totalEscrowApplied;

    /// @dev mapping of the escrow applied from the data retrieval entity to the destination. escrow_ready_to_settle = escrowApplied[dataRetrievalEntityAddress][destinationAddressThatCanRedeem]
    mapping (address => mapping(address => uint256)) escrowApplied;

    /// @dev mapping of the escrow applied to the target. escrow_ready_to_settle = escrowAppliedSum[destinationAddressThatCanRedeem]
    mapping (address => uint256) escrowAppliedSum;

    /// @dev The owner of this contract.
    address owner = address(0);

    /// @dev The Trust Anchor Manager address that this contract is bound to.
    address trustAnchorManagerAddress = address(0);

    /// @dev The Trust Anchor Storage address that this contract is bound to.
    address trustAnchorStorageAddress = address(0);

    /// @dev Initializes this contract with the owner set to the calling public address.

    constructor() {
        owner = msg.sender;
    }

    /// @param _trustAnchorManagerAddress The smart contract address for the Trust Anchor Manager that should be linked.
    /// @dev Links Trust Anchor Manager to this contract's function flow.
    /// @return result
    ///    | false: [ will never return false due to require() ]
    ///    | true: trust anchor manager address set correctly

    function setTrustAnchorManagerAddress(address _trustAnchorManagerAddress) public returns (bool result) {
        require(_trustAnchorManagerAddress != address(0));
        require(msg.sender == owner);

        trustAnchorManagerAddress = _trustAnchorManagerAddress;

        //trust anchor manager address set correctly
        return true;
    }

    /// @param _trustAnchorStorageAddress The smart contract address for the Trust Anchor Storage that should be linked.
    /// @dev Links Trust Anchor Storage to this contract's function flow.
    /// @return result
    ///    | false: [ will never return false due to require() ]
    ///    | true: trust anchor storage address set correctly

    function setTrustAnchorStorageAddress(address _trustAnchorStorageAddress) public returns (bool result) {
        require(_trustAnchorStorageAddress != address(0));
        require(msg.sender == owner);

        trustAnchorStorageAddress = _trustAnchorStorageAddress;

        //trust anchor storage address set correctly
        return true;
    }

    // those applying escrow need to be trust anchors
    // workflow looks like:

    // 1) attestation is set
    // 2a) managed (user gives pii-holding trust anchor permission to deal with data transfer, select targets, etc.) sets a data retrieval target (must be valid ta)
    // 2b) unmanaged (user gives permission/consent to pii-holding trust anchor to transfer data to another trust anchor manually) sets a data retrieval target (must be valid ta)
    // 3) destination trust anchor puts escrow funds (SHFT tokens) into this smart contract via the "applyEscrow" function. this specific smart contract *only* uses SHFT tokens to transfer, this can be extended by either re-linking an upgraded version of this smart contract on the trust anchor manager or extending a SHFT<->Bancor (or equivalent) token bridge.
    // 4a) source trust anchor, watching this contract's events, sees that this escrow event has occurred. that means it can send its data depending on:
    // 4a i) the "contestation oracle", an address that can sign to contest (cancel) an escrow even if the documents have been sent. This "contestation oracle" can be equal to address(0) (a null address, and thus no contestation oracle), or the data retrieval entity itself.
    // 4a ii) the "contestation time period", the amount of time after the sender has triggered a "sent the documents" message that the predetermined oracle has to respond and perform the contestation step if they determine the quality/veracity of the documents sent are questionable.
    // 4b i) source trust anchor does (an offchain) transfer of user's consented pii data, with the consented data structure available via encrypted bytes in step 2.
    // 4b ii) source trust anchor triggers the "setDataSenderDidSendDocuments" function in the Trust Anchor Storage. This indicates the transfer of source documents, and gives the oracle (4a i) the predetermined time (4a ii) to respond to the quality of the sent documents.
    // 5) destination trust anchor, having received user's pii data, confirms all of the documents transferred match the consented data structure (at this point both ta's have signed/sent at least one message to the trust anchor manager smart contract, so their public keys are available bilaterally for encrypted communications).
    // 6) destination trust anchor triggers the "dataRetrievalEntityValidateAttestation" function in the trust anchor storage smart contract, completing the chain-of-custody relationship.
    // 7a) the source trust anchor then triggers the "settleEscrow" function, which confirms that step 6 was performed, and releases the escrow to the source trust anchor.
    // 7b) the end-user may trigger the "settleEscrow" function at any point after step 6.
    // 8) after 30 days, the data retrieval target may cancel their escrow if the sender has not triggered the "sent the documents" message.

    function applyEscrow(bytes32 _attestationKeccak, address _contestationOracleAddress, uint256 _contestationTimePeriod) public payable returns (uint8 result) {
        ITrustAnchorManager trustAnchorManager = ITrustAnchorManager(trustAnchorManagerAddress);
        ITrustAnchorStorage trustAnchorStorage = ITrustAnchorStorage(trustAnchorStorageAddress);

        address currentRetrieverAddress = msg.sender;

        // those applying escrow need to be trust anchors
        if (trustAnchorManager.isTrustAnchorVerified(currentRetrieverAddress)) {
            address trustAnchorAddress = trustAnchorStorage.getAttestationTrustAnchorAddress(_attestationKeccak);
            address identifiedAddress = trustAnchorStorage.getAttestationOriginalIdentifiedAddress(_attestationKeccak);

            uint256 dataRetrievalRevenueSharePercentage = trustAnchorStorage.getAttestationIdentifiedAddressToTrustAnchorDataRetrievalRevenueSharePercentage(_attestationKeccak);

            // those applying escrow need to be valid data retrievers for this attestation.
            if (trustAnchorStorage.getAttestationIsRetrievableFromRetriever(_attestationKeccak, currentRetrieverAddress)) {
                // the user's share of revenue cannot be over 100%.
                if (dataRetrievalRevenueSharePercentage <= 100000000) {
                    // add the total escrow applied to the contract for bookkeeping.
                    totalEscrowApplied = totalEscrowApplied.add(msg.value);

                    // 6 decimals for the input precision (0.000001% minimum sharing = "1", "1000000" = 1.0%, "100000000" = 100.0%)
                    int128 userDataRetrievalShare64x64 = ABDKMath64x64.divi(int256(dataRetrievalRevenueSharePercentage), 100000000);

                    // do some fixed point math to figure out what the user's share of the assets is.
                    uint256 userDataRetrievalShare = uint256(ABDKMath64x64.muli(userDataRetrievalShare64x64, int256(msg.value)));

                    uint256 trustAnchorDataRetrievalShare = msg.value.sub(userDataRetrievalShare);

                    // the trust anchor's share is the total sent (msg.value) minus the user's share.
                    escrowApplied[currentRetrieverAddress][identifiedAddress] = escrowApplied[currentRetrieverAddress][identifiedAddress].add(userDataRetrievalShare);
                    escrowApplied[currentRetrieverAddress][trustAnchorAddress] = escrowApplied[currentRetrieverAddress][trustAnchorAddress].add(trustAnchorDataRetrievalShare);

                    escrowAppliedSum[identifiedAddress] = escrowAppliedSum[identifiedAddress].add(userDataRetrievalShare);
                    escrowAppliedSum[trustAnchorAddress] = escrowAppliedSum[trustAnchorAddress].add(trustAnchorDataRetrievalShare);

                    // get the "escrow" struct data pointer from storage
                    escrow storage newEscrow = attestationEscrow[_attestationKeccak][currentRetrieverAddress];

                    // in the case where an escrow for this attestation from this retrieval entity already exists, increment the escrow amount.
                    if (newEscrow.isActive == true) {
                        // update the amounts based on the message's value (the amount of native gas-equivalent units sent into
                        // this contract from the data retrieval entity for the purpose of escrow.
                        newEscrow.totalAmount = newEscrow.totalAmount.add(msg.value);
                        newEscrow.userAmount = newEscrow.userAmount.add(userDataRetrievalShare);
                        newEscrow.trustAnchorAmount = newEscrow.trustAnchorAmount.add(trustAnchorDataRetrievalShare);

                        emit EVT_updatedEscrow(_attestationKeccak, currentRetrieverAddress, newEscrow.totalAmount, trustAnchorDataRetrievalShare);

                        //escrow was updated
                        return 4;
                    } else {
                        // otherwise, set this attestation's base values (ownership, timestamps, et al.)
                        newEscrow.isActive = true;
                        // clear the previous completion or cancellation if it already exists (re-sending data for example)
                        newEscrow.wasCompleted = false;
                        newEscrow.wasCancelled = false;

                        newEscrow.sentFrom = currentRetrieverAddress;

                        // the data retrieval entity sets these in the function initalizer, signalling to the source trust
                        // anchors the parameters for a "contestation" step, where there is a limited time from signalling
                        // "sent" by the sender, to the oracle (either "none" via address(0), another party that will manage
                        // this relationship as a trusted third party, or the address of the data retrieval entity themselves.

                        newEscrow.contestationOracleAddress = _contestationOracleAddress;
                        newEscrow.contestationTimePeriod = _contestationTimePeriod;

                        // set the original timestamp of when this attestation's escrow pool from the retrieval entity was set.
                        newEscrow.setTimestamp = block.timestamp;

                        // set the amounts based on the message's value (the amount of native gas-equivalent units sent into
                        // this contract from the data retrieval entity for the purpose of escrow.
                        newEscrow.totalAmount = msg.value;
                        newEscrow.userAmount = userDataRetrievalShare;
                        newEscrow.trustAnchorAmount = trustAnchorDataRetrievalShare;

                        emit EVT_appliedEscrow(_attestationKeccak, currentRetrieverAddress, newEscrow.totalAmount, trustAnchorDataRetrievalShare);

                        //escrow was set
                        return 3;
                    }
                } else {
                    // end-user's data retrieval share percentage is > 100%, this isn't valid.
                    //@note: must revert() due to asset transactions already having occurred.
                    revert();
//                        return 2;
                }
            } else {
                //this trust anchor does not have permission to retrieve this asset.
                //@note: must revert() due to asset transactions already having occurred.
                revert();
//                    return 1;
            }
        } else {
            //not a valid trust anchor
            //@note: must revert() due to asset transactions already having occurred.
            revert();
//                return 0;
        }
    }

    // there needs to be a setup where the escrow can be revoked, before the dataRetrievalEntityValidateAttestation step, but not after that step.

    // there are 2 cases where escrow can be reverted:
    // 1) if the total escrow expiry time passes (30 days), the trust anchor that set the escrow can receive it back if the sending trust anchor does not confirm they have sent the documents.

    // 2) after confirming sending of the documents, there is a (24 hour) window where this receipt of the documents can be confirmed by an additional party that both trust anchors trust to perform a cancellation on the escrow (an external oracle).

    function getOracleCanContestEscrow(bytes32 _attestationKeccak, address _dataRetrievalEntityAddress) public view returns (uint8 result) {
        ITrustAnchorStorage trustAnchorStorage = ITrustAnchorStorage(trustAnchorStorageAddress);

        escrow storage existingEscrow = attestationEscrow[_attestationKeccak][_dataRetrievalEntityAddress];

        if (existingEscrow.isActive == true) {
            bool canCancelEscrow = false;

            if (existingEscrow.contestationOracleAddress != address(0)) {
                //@note: for block.timestamp, any miners could not cheat in the timestamp by a tolerance of 900 seconds. so if you check outside this interval you are safe.
                bool senderDidSendDocuments = trustAnchorStorage.getAttestationDataWasSentToRetrievalEntity(_attestationKeccak, _dataRetrievalEntityAddress);
                uint256 sentDocumentsTimestamp = trustAnchorStorage.getAttestationDataWasSentToRetrievalEntityTimestamp(_attestationKeccak, _dataRetrievalEntityAddress);

                if (senderDidSendDocuments == true && (block.timestamp <= sentDocumentsTimestamp + existingEscrow.contestationTimePeriod)) {
                    canCancelEscrow = true;
                }
            }

            if (canCancelEscrow == true) {
                //oracle can cancel escrow at the current time
                return 2;
            } else {
                //oracle cannot cancel escrow
                return 1;
            }
        } else {
            //escrow does not exist
            return 0;
        }
    }

    function contestEscrow(bytes32 _attestationKeccak, address _dataRetrievalEntityAddress) public returns (uint8 result) {
        if (getOracleCanContestEscrow(_attestationKeccak, _dataRetrievalEntityAddress) == 2) {

            escrow storage existingEscrow = attestationEscrow[_attestationKeccak][_dataRetrievalEntityAddress];

            if (existingEscrow.contestationOracleAddress == msg.sender) {
                existingEscrow.oracleDidContest = true;
                //oracle successfully contested

                emit EVT_contestEscrow(_attestationKeccak, _dataRetrievalEntityAddress, existingEscrow.contestationOracleAddress);

                return 2;
            } else {
                //message sender is not the contestation oracle
                return 1;
            }
        } else {
            //no oracle can contest escrow
            return 0;
        }
    }

    function cancelEscrow(bytes32 _attestationKeccak) public returns (uint8 result) {
        ITrustAnchorStorage trustAnchorStorage = ITrustAnchorStorage(trustAnchorStorageAddress);

        address currentRetrieverAddress = msg.sender;

        escrow storage existingEscrow = attestationEscrow[_attestationKeccak][currentRetrieverAddress];
        if (existingEscrow.sentFrom == currentRetrieverAddress) {
            address trustAnchorAddress = trustAnchorStorage.getAttestationTrustAnchorAddress(_attestationKeccak);
            address identifiedAddress = trustAnchorStorage.getAttestationOriginalIdentifiedAddress(_attestationKeccak);

            bool sourceDataWasRetrieved = trustAnchorStorage.getAttestationIsValidFromRetriever(_attestationKeccak, currentRetrieverAddress);

            if (sourceDataWasRetrieved == false) {
                //@note: for block.timestamp, any miners could not cheat in the timestamp by a tolerance of 900 seconds. so if you check outside this interval you are safe.
                bool senderDidSendDocuments = trustAnchorStorage.getAttestationDataWasSentToRetrievalEntity(_attestationKeccak, currentRetrieverAddress);

                bool senderDidNotTransmitDocumentsIn30Days = ((senderDidSendDocuments == false) && (block.timestamp > existingEscrow.setTimestamp + baseEscrowExpiryTime));

                //if either the oracle explicitly cancelled the escrow, *or* the documents were not sent in 30 days, the escrow can be cancelled.
                if (existingEscrow.oracleDidContest == true || senderDidNotTransmitDocumentsIn30Days == true) {
                    existingEscrow.isActive = false;
                    existingEscrow.wasCancelled = true;

                    //reverts all the internal contract balances to reflect the cancellation.
                    totalEscrowApplied = totalEscrowApplied.sub(existingEscrow.totalAmount);

                    escrowApplied[currentRetrieverAddress][identifiedAddress] = escrowApplied[currentRetrieverAddress][identifiedAddress].sub(existingEscrow.userAmount);
                    escrowApplied[currentRetrieverAddress][trustAnchorAddress] = escrowApplied[currentRetrieverAddress][trustAnchorAddress].sub(existingEscrow.trustAnchorAmount);

                    escrowAppliedSum[identifiedAddress] = escrowAppliedSum[identifiedAddress].sub(existingEscrow.userAmount);
                    escrowAppliedSum[trustAnchorAddress] = escrowAppliedSum[trustAnchorAddress].sub(existingEscrow.trustAnchorAmount);

                    //sends the native gas-equivalent tokens back to the original sender of the escrow.

                    //@note:@test:@internalaudit: make sure to check for double-entry issues.. I believe everything is properly closed off at the moment,
                    // but what happens if it triggers another "applyEscrow".. is that an issue at all?

                    emit EVT_cancelEscrow(_attestationKeccak, existingEscrow.sentFrom, totalEscrowApplied);

                    (bool success, ) = existingEscrow.sentFrom.call{value: existingEscrow.totalAmount}("");
                    require(success, "did not output correctly");

                    //escrow reverted
                    return 3;
                } else {
                    //either the oracle didn't explicitly cancel the escrow (or) the time period of the document sending timeout has not occurred yet
                    return 2;
                }
            } else {
                //source data was already retrieved
                return 1;
            }
        } else {
            //not the entity that originally set the escrow
            return 0;
        }
    }

    ///notice: Escrow-receiving party doesn't need to be an active trust anchor, they simply need to have been a trust anchor in the past. (ie. even if they've been removed from the system, their past actions of data transmission are valid, preventing malicious extortion vectors from the upper-level trust anchor manager administrators)
    // alternatively, the identifiedAddress (end-user) can be used to redeem their proportion of the data retrieval revenue share.

    function settleEscrow(bytes32[] memory _attestationKeccaks, address[] memory _retrieverAddresses, address _outputAddress) public returns (uint8 result) {
        ITrustAnchorStorage trustAnchorStorage = ITrustAnchorStorage(trustAnchorStorageAddress);

        bool escrowWasProcessed = false;

        uint256 accumulatedAmount = 0;

        if (_attestationKeccaks.length == _retrieverAddresses.length) {
            for (uint i = 0; i < _attestationKeccaks.length; i++) {
                bytes32 curAttestationKeccak = _attestationKeccaks[i];
                address curRetrieverAddress = _retrieverAddresses[i];
                address trustAnchorAddress = trustAnchorStorage.getAttestationTrustAnchorAddress(curAttestationKeccak);
                address identifiedAddress = trustAnchorStorage.getAttestationOriginalIdentifiedAddress(curAttestationKeccak);

                escrow storage existingEscrow = attestationEscrow[curAttestationKeccak][curRetrieverAddress];
//                emit EVT_logEscrow(existingEscrow);
                if (existingEscrow.isActive == true) {
                    //reference the attestation by the original identified address
                    bool sourceDataWasRetrieved = trustAnchorStorage.getAttestationIsValidFromRetriever(curAttestationKeccak, curRetrieverAddress);

                    bool canProcessSettlement = false;
                    // the easiest case is that the data retrieval entity marked this document as "valid".
                    if (sourceDataWasRetrieved == true) {
                        canProcessSettlement = true;
                    } else {
                        // and more the more complicated one is the contestation window.. if the sender sets their "I've sent
                        // the source data" message, from that point until the contestationTimePeriod ends, this escrow
                        // cannot be "settled", by any party. If they haven't sent that message, the data retrieval entity
                        // can cancel the escrow 30 days after first setting it.

                        // data retrieval entities will never process a "valid" source document transfer while in a contestation
                        // phase unless they do not wish to activate the arbitration.

                        uint256 sentDocumentsTimestamp = trustAnchorStorage.getAttestationDataWasSentToRetrievalEntityTimestamp(curAttestationKeccak, curRetrieverAddress);
                        if (sentDocumentsTimestamp > 0) {
                            uint256 settlementWindow = 0;

                            if (existingEscrow.contestationOracleAddress != address(0)) {
                                settlementWindow = existingEscrow.contestationTimePeriod;
                            }

                            if (block.timestamp > sentDocumentsTimestamp + settlementWindow) {
                                canProcessSettlement = true;
                            }
                        }
                    }

                    if (canProcessSettlement == true) {
                        bool processUser = false;
                        bool processTrustAnchor = false;

                        if (msg.sender == identifiedAddress) {
                            if (existingEscrow.hasUserSettled == false) {
                                processUser = true;
                            }
                        }

                        if (msg.sender == trustAnchorAddress) {
                            if (existingEscrow.hasTrustAnchorSettled == false) {
                                processTrustAnchor = true;
                            }
                        }

                        if (processUser == true) {
                            //output the end user's escrow amount to their identified address.
                            accumulatedAmount = accumulatedAmount.add(existingEscrow.userAmount);

                            //reduce total escrow values by the amount redeemed
                            escrowApplied[curRetrieverAddress][identifiedAddress] = escrowApplied[curRetrieverAddress][identifiedAddress].sub(existingEscrow.userAmount);
                            escrowAppliedSum[identifiedAddress] = escrowAppliedSum[identifiedAddress].sub(existingEscrow.userAmount);

                            totalEscrowApplied = totalEscrowApplied.sub(existingEscrow.userAmount);

                            existingEscrow.hasUserSettled = true;

                            if (existingEscrow.hasTrustAnchorSettled == true) {
                                existingEscrow.wasCompleted = true;
                                existingEscrow.isActive = false;
                            }

                            emit EVT_settledRecipient(curAttestationKeccak, curRetrieverAddress, identifiedAddress, existingEscrow.userAmount, existingEscrow.wasCompleted);
                        }

                        if (processTrustAnchor == true) {
                            //output the trust anchor's escrow amount to their trust anchor address.
                            accumulatedAmount = accumulatedAmount.add(existingEscrow.trustAnchorAmount);

                            //reduce total escrow values by the amount redeemed
                            escrowApplied[curRetrieverAddress][trustAnchorAddress] = escrowApplied[curRetrieverAddress][trustAnchorAddress].sub(existingEscrow.trustAnchorAmount);
                            escrowAppliedSum[trustAnchorAddress] = escrowAppliedSum[trustAnchorAddress].sub(existingEscrow.trustAnchorAmount);

                            totalEscrowApplied = totalEscrowApplied.sub(existingEscrow.trustAnchorAmount);

                            existingEscrow.hasTrustAnchorSettled = true;

                            if (existingEscrow.hasUserSettled == true) {
                                existingEscrow.wasCompleted = true;
                                existingEscrow.isActive = false;
                            }

                            emit EVT_settledRecipient(curAttestationKeccak, curRetrieverAddress, trustAnchorAddress, existingEscrow.trustAnchorAmount, existingEscrow.wasCompleted);
                        }

                        if (processTrustAnchor == true || processUser == true) {
                            //redeemed escrow amount
                            escrowWasProcessed = true;
                            continue;
                        } else {
                            //escrow not redeemed
                            continue;
                        }
                    } else {
                        //cannot process settlement (source data not sent, or contestation period still in effect)
                        continue;
                    }
                } else {
                    //escrow is not active for this attestation and receiver
                    continue;
                }
            }

            emit EVT_D(accumulatedAmount);

            if (escrowWasProcessed == true) {
                // send the accumulated amount to the output address.
                (bool success, ) = _outputAddress.call{value: accumulatedAmount}("");
                require(success, "did not output correctly");

                //escrow was processed for input parameters
                return 2;
            } else {
                //no escrow processed for input parameters
                return 1;
            }
        } else {
            //length of attestations array is not identical to the length of the retriever addresses array
            return 0;
        }
    }

    function getEscrow(bytes32 _attestationKeccak, address _dataRetrievalEntityAddress) public view returns (address sentFrom, uint256 totalAmount, uint256 userAmount, uint256 trustAnchorAmount, address contestationOracleAddress, uint256 contestationTimePeriod, uint256 setTimestamp, bool wasCompleted, bool wasCancelled) {
        escrow storage existingEscrow = attestationEscrow[_attestationKeccak][_dataRetrievalEntityAddress];

        return (existingEscrow.sentFrom,
                existingEscrow.totalAmount,
                existingEscrow.userAmount,
                existingEscrow.trustAnchorAmount,
                existingEscrow.contestationOracleAddress,
                existingEscrow.contestationTimePeriod,
                existingEscrow.setTimestamp,
                existingEscrow.wasCompleted,
                existingEscrow.wasCancelled);
    }

    function getEscrowBalanceFromRetriever(address _dataRetrievalEntityAddress, address _escrowRecipientAddress) public view returns (uint256 balance) {
        return escrowApplied[_dataRetrievalEntityAddress][_escrowRecipientAddress];
    }

    function getEscrowBalanceSum(address _escrowRecipientAddress) public view returns (uint256 balance) {
        return escrowAppliedSum[_escrowRecipientAddress];
    }

    function getBaseEscrowExpiryTime() public view returns (uint256 timeAmount) {
        return baseEscrowExpiryTime;
    }
}

