pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "../Interfaces/IErc223.sol";
import "../Libraries/SafeMath.sol";

contract Erc223Reference is IErc223, IErc223ReceivingContract {
    using SafeMath for uint256;

    event EVT_transferredToConduitCustody(address _to, uint256 _value, bytes _data, bytes4 shyftConduitSig, bytes4 sig);

    mapping(address => uint) balances;

    string public name    = "Token";
    string public symbol  = "TKN";
    uint8 public decimals = 18;
    uint256 thisTotalSupply;

    uint256 public tokenPurchaseMultiplier = 1;

    address public owner;

    //adding a bit for contract whitelisting to flex the tokenFallback functionality a bit.
    mapping (address => bool) tokenContractWhitelist;

    //signature for triggering 'tokenFallback' in erc223 receiver contracts
    bytes4 shyftConduitSig = bytes4(keccak256("fromShyftConduit(address,address,uint256)")); // function signature

    constructor (
        uint256 _initialAmount,
        string memory _tokenName,
        uint8 _decimalUnits,
        string memory _tokenSymbol
    ) {
        balances[msg.sender] = _initialAmount;               // Give the creator all initial tokens
        thisTotalSupply = _initialAmount;                        // Update total supply
        name = _tokenName;                                   // Set the name for display purposes
        decimals = _decimalUnits;                            // Amount of decimals for display purposes
        symbol = _tokenSymbol;                               // Set the symbol for display purposes
        owner = msg.sender;
    }

    function totalSupply() public view override returns (uint256) {
        return thisTotalSupply;
    }

    function getIsTokenContractWhiteListed(address _address) public view returns (bool result) {
        return tokenContractWhitelist[_address];
    }

    function setIsTokenContractWhiteListed(address _address, bool _isWhiteListed) public returns (bool result) {
        require(msg.sender == owner);

        tokenContractWhitelist[_address] = _isWhiteListed;

        return true;
    }

    // Function that is called when a user or another contract wants to transfer funds .
    function transfer(address _to, uint _value, bytes memory _data, string memory _custom_fallback) public returns (bool success) {

        if(isContract(_to)) {
            if (balanceOf(msg.sender) < _value) revert();
            balances[msg.sender] = balances[msg.sender].sub(_value);
            balances[_to] = balances[_to].add(_value);

            (success, ) = _to.call{value:0}(abi.encodeWithSelector(bytes4(keccak256(abi.encodePacked(_custom_fallback))), msg.sender, _value, _data));
            require(success == true);

            emit Transfer(msg.sender, _to, _value, _data);
            return true;
        }
        else {
            return transferToAddress(_to, _value, _data);
        }
    }


    // Function that is called when a user or another contract wants to transfer funds .
    function transfer(address _to, uint _value, bytes memory _data) public override returns (bool success) {

        if(isContract(_to)) {
            return transferToContract(_to, _value, _data);
        }
        else {
            return transferToAddress(_to, _value, _data);
        }
    }

    // Standard function transfer similar to ERC20 transfer with no _data .
    // Added due to backwards compatibility reasons .
    function transfer(address _to, uint _value) public override returns (bool success) {

        //standard function transfer similar to ERC20 transfer with no _data
        //added due to backwards compatibility reasons
        bytes memory empty;
        if(isContract(_to)) {
            return transferToContract(_to, _value, empty);
        }
        else {
            return transferToAddress(_to, _value, empty);
        }
    }

    //assemble the given address bytecode. If bytecode exists then the _addr is a contract.
    function isContract(address _addr) private view returns (bool is_contract) {
        uint length;
        assembly {
        //retrieve the size of the code on target address, this needs assembly
            length := extcodesize(_addr)
        }
        return (length>0);
    }

    //function that is called when transaction target is an address
    function transferToAddress(address _to, uint _value, bytes memory _data) private returns (bool success) {
        if (balanceOf(msg.sender) < _value) revert();
        balances[msg.sender] = balances[msg.sender].sub(_value);
        balances[_to] = balances[_to].add(_value);
        emit Transfer(msg.sender, _to, _value, _data);
        return true;
    }

    //function that is called when transaction target is a contract
    function transferToContract(address _to, uint _value, bytes memory _data) private returns (bool success) {
        if (balanceOf(msg.sender) < _value) revert();
        balances[msg.sender] = balances[msg.sender].sub(_value);
        balances[_to] = balances[_to].add(_value);

        IErc223ReceivingContract receiver = IErc223ReceivingContract(_to);
        receiver.tokenFallback(msg.sender, _value, _data);
        emit Transfer(msg.sender, _to, _value, _data);
        return true;
    }


    function balanceOf(address _owner) public view override returns (uint balance) {
        return balances[_owner];
    }

    //@conduit:
    //@note: this function has been modified to be a reference for the shyft conduit incoming conterminous token output.
    function tokenFallback(address _to, uint256 _value, bytes memory _data) public override returns (bool ok) {
        if (tokenContractWhitelist[msg.sender] == true) {
            bytes4 sig = bytes4(uint32(bytes4(bytes1(_data[3])) >> 24) + uint32(bytes4(bytes1(_data[2])) >> 16) + uint32(bytes4(bytes1(_data[1])) >> 8) + uint32(bytes4(bytes1(_data[0]))));
            emit EVT_transferredToConduitCustody(_to, _value, _data, shyftConduitSig, sig);

            if (sig == shyftConduitSig) {
                if (balances[msg.sender] >= _value) {
                    balances[msg.sender] = balances[msg.sender].sub(_value);
                    balances[_to] = balances[_to].add(_value);

                    return true;
                } else {
                    return false;
                }
            } else {
                return false;
            }
        } else {
            return true;
        }
    }

    receive() external payable {
        uint256 totalAddedTokens = msg.value.mul(tokenPurchaseMultiplier);

        balances[msg.sender] = balances[msg.sender].add(totalAddedTokens);
        thisTotalSupply = thisTotalSupply.add(totalAddedTokens);
    }
}
