//@note:@todo:@next: build a tool that allow an attestation from the bridge to other accounts to become
// possible, tracking the originating blockchain account's immediate attestation settings. A trust channel
// map could theoretically be able to be done.

/*
    **** The Shyft Bridge ****

    This smart contract is the primary mechanism for the book-keeping (not the cross chain architecture that does
    oracalized bridging) for the Shyft Bridge. There are many functions that relate to both the entry into the bridge
    (transitAsset for example) and functions on the exit of the bridge (withdrawAsset for example).

    All entrances are guarded by Safe Holds being timelocked up until the point where transitAsset is called (holds are
    done by timestamps based on expiry time (the Safe's holdSafeBalanceForTypeWithPermissions for example), with
    permissions for the Bridge itself to aquire those synthetic assets, burn them on the source chain, and create them
    on the withdrawal chain.

    Both transaction roots and transaction receipt roots are required for proof of proper transfer.

    Upon withdrawal, funds are created as Safe Holds within the destination address's control on the destination chain,
    thus can be converted into either the synthetic *or* if the destination chain's native fuel *is* the synthetic asset
    type, it can be converted to native fuel re: ShyftKycContract (which inherits the Safe functionality).
*/



pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED

import "./Interfaces/IShyftSafe.sol";
import "./Libraries/MerklePatriciaProof.sol";

import "./Libraries/SafeMath.sol";

import "./Constants/TokenConstants.sol";

import "./Libraries/BytesLib.sol";

import "./RelayerElection.sol";

contract ShyftBridge is RelayerElection, TokenConstants {
    using SafeMath for uint256;

    event EVT_RootStorage(address chain, uint256 start,
        uint256 indexed end, bytes32 indexed headerRoot, uint indexed i);

    event EVT_TransitAsset(address indexed shyftSafe, address indexed toChain, address indexed toAddress, uint256 bip32X_type,
        uint256 amount, address fromChain);

    event EVT_WithdrawAsset(address indexed shyftSafe, address indexed fromChain,
        address indexed fromAddress, uint256 bip32X_type, uint256 amount);

    event EVT_TokenAdded(address indexed fromChain, uint256 indexed bip32X_type_orig,
        uint256 indexed bip32X_type_new);

    event EVT_TokenAssociated(address indexed toChain, uint256 indexed bip32X_type_from,
        uint256 indexed bip32X_type_to);

//    event EVT_withdrawAsset(bool hasBeenSetup, bytes32 rlpWithdrawTx, uint256 withdrawalPrepareBlockNumber, uint256 blockTime, uint256 confirmationTime);

    event EVT_PreparedWithdrawal(address _user, uint256 _bip32X_type, uint256 _amount, bytes32 _txRoot);

    event EVT_WithdrawTest(bytes32 _receiptsRoot, bytes32 leaf);

    event EVT_ProveWithdrawalReceipt(address _to, bytes32 _receiptsRoot);

    event EVT_confirmTxContractParameters(address _address, uint256 amount, uint256 bip32X_type, address proofAddress, uint256 proofAmount, uint256 proofBip32XType, address _thisChain, address proofToChain);
//    event EVT_debugRLPTransitTx(uint256 nonce, uint256 gasPrice, uint256 gas, address to, uint256 amount, bytes input, bytes32 v, bytes32 r, bytes32 s, bytes32 inputHash);

    // The user prepares a withdrawal with Safe data and then releases it with a withdraw.
    struct Withdrawal {
        uint256 bip32X_type;
        address fromChain;
        address safeAddress;
        uint256 amount;
        bytes32 txRoot;
        bytes32 receiptsRoot;
        uint256 withdrawalPrepareBlockNumber;

        bool hasBeenSetup;
        bool hasBeenProcessed;
    }

    address public shyftSafeAddress = address(0);

    IShyftSafe shyftSafe;

    // The key is a shyftSafe address and the rlpWithdrawTx, and the value is a withdrawal struct.
    mapping(address => mapping(bytes32 => Withdrawal)) userWithdrawals;

    // Roots of merkle trees made up of consecutive block headers
    // The key is the bridged chain and the value is the corresponding root.
    mapping(address => bytes32[]) roots;

    // Tracking the last block for each bridge network
    mapping(address => uint256) lastBlock;

    // Tokens need to be associated between chains.
    // fromChainAddress => (oldToken => newToken)
    mapping(address => mapping(uint32 => uint32)) tokens;

    uint256 relayerThreshold = 1;

    struct tokenChainInfo {
        bool isSetup;
        bool isTestnet;
        bool isPrivatenet;

        uint256 nativeTokenType;

        mapping(uint256 => bool) bip32X_typeTokenAllowed;
        mapping(uint256 => bool) bip32X_typeTokenDisallowed;

        uint256 transitBondBip32X_type;
        uint256 transitBondAmount;

        mapping(uint256 => uint256) confirmationBlocksRequired;
        mapping(uint256 => uint256) baseConfirmationBlocksRequired;

        uint256 averageBlockTime;
    }

    mapping(address => tokenChainInfo) tokenChains;

    constructor(uint256 _nativeBip32X_type) {
        owner = msg.sender;
        Relayer memory temp;
        temp.addr = msg.sender;
        nextRelayer = temp;

        uint256 averageBlockTime = 15;

        uint256 blocksRequired = 6;
        //set initial confirmation time for 1 minute
        tokenChains[address(this)].averageBlockTime = averageBlockTime;
        tokenChains[address(this)].nativeTokenType = _nativeBip32X_type;

        tokenChains[address(this)].baseConfirmationBlocksRequired[_nativeBip32X_type] = blocksRequired;
        tokenChains[address(this)].confirmationBlocksRequired[_nativeBip32X_type] = blocksRequired;
    }

    /**************** RELAYER FUNCTIONS *************/

    // @dev: Since we are assuming Shyft is honest, there's no need to propose a root.
    // Thus, we can simply set an appropriate root.
    //
    // @dev: This function saves a hash to an append-only array of block header hashes
    // associated with the given origin chain address-id.
    //
    // @param _root , the root we want to add
    // @param _chainId, the bridge contract addess on the bridged network from which the header root comes from
    // @param _end, index to ensure that we are indeed adding blocks
    // @param _sig, array of signatures of relayers signing [_root, _chainId, _start, _end]
    function addRoot(bytes32 _root, address _chainId, uint256 _start, uint256 _end, bytes memory _sig) public returns (uint) {
      // Make sure we are adding blocks
      require(_start == lastBlock[_chainId] + 1 && _end > _start);

      // Only currently elected relayer can add a root.
      require(msg.sender == nextRelayer.addr);

      // Ensure that the other relayers sign off on the header root
      require(checkSignatures(_root, _chainId, _start, _end, _sig) >= relayerThreshold);

      // Add the header roots
      roots[_chainId].push(_root);

      // Log that Shyft has added a root
      emit EVT_RootStorage(_chainId, _start, _end, _root, roots[_chainId].length - 1);
      lastBlock[_chainId] = _end;

      return roots[_chainId].length;
    }

    /**************** USER/SHYFT SAFE FUNCTIONS *************/

    // @note: a (wallet) user calls this function to begin the process of "transiting" (moving) their assets across
    // blockchains.
    //
    // the user needs to have assets currently within their Safe hold(s). as long as the asset is with their Safe hold(s)
    // it is guaranteed to be a balance they can transit. in order to induce a proper locking mechanism, the asset has a
    // minimum block confirmation of 6 blocks
    //
    // additionally, there is a requirement for a bond to be passed in that is valid.

    //@note:@todo:@future: integrate IEV with bonding mechanism
//    function setIEVForAsset(uint256 _bip32X_type) public returns (uint8 result) {
//
//    }
//
//    function getIEVForAsset(uint256 _amount, uint256 _bip32X_type) public returns (uint256 result) {
//    }

    //@note:@todo:@critical: create full tests for this.
    //returns:
    // false = token bip32X_type not allowed for transit
    // true = token bip32X_type allowed for transit

    function getBip32X_typeAllowedForTransit(address _toChain, uint256 _bip32X_type) public view returns (bool result) {
        bool passedWhitelist = true;
        bool passedBlacklist = true;

        tokenChainInfo storage tokenChain = tokenChains[_toChain];

        //@note: special requirements for testnet and privatenet
        if (tokenChain.isTestnet == true || tokenChain.isPrivatenet == true) {
            //native types are always allowed (1:1 backed at all times)
            if (_bip32X_type != tokenChain.nativeTokenType) {
                //specific testnet tokens can be relayed, but the destination chain must support this
                if (tokenChain.bip32X_typeTokenAllowed[_bip32X_type] == false) {
                    passedWhitelist = false;
                }
            }
        } else {
            //otherwise, everything is allowed that is not on the list
            if (tokenChain.bip32X_typeTokenDisallowed[_bip32X_type] == true) {
                passedBlacklist = false;
            }
        }

        if (passedWhitelist == true && passedBlacklist == true) {
            //token bip32X_type allowed for transit
            result = true;
        } else {
            //token bip32X_type not allowed for transit
            result = false;
        }

        return result;
    }

    function getBondPassesBalanceRequirementsForTransit(address _address, uint256 _holdIndexBond) public view returns (bool result) {
        bool isApproved = false;

        if (tokenChains[address(this)].transitBondAmount > 0) {
            uint256 holdAmount = shyftSafe.getAmountForHold(_address, _holdIndexBond);
            uint256 holdBip32X_type = shyftSafe.getBip32XTypeForHold(_address, _holdIndexBond);

            if (holdAmount >= tokenChains[address(this)].transitBondAmount &&
            holdBip32X_type == tokenChains[address(this)].transitBondBip32X_type) {
                isApproved = true;
            }

            //@note:@todo:@future: integrate IEV with bonding mechanism
            //        uint256 totalRemaining = bridgeConfig.totalRequirement;

            //        for (uint256 i = 0; i < bridgeConfig.bondTypesApproved.length; i++) {
            //            uint256 currentBalance = getIEVForAsset(shyftSafe.getSafeBalance(_address, bridgeConfig.bondTypesApproved[i]), bridgeConfig.bondTypesApproved[i]);
            //            if (currentBalance >= totalRemaining) {
            //                isApproved = true;
            //            } else {
            //                //@note: no need for safemath given the subtraction is already bounds-checked
            //                totalRemaining -= currentBalance;
            //            }
            //        }
        } else {
            //no bond was set, so passes automatically
            isApproved = true;
        }

        return isApproved;
    }

    //returns:
    // 0 = token type not allowed for transit
    // 1 = hold index cannot equal the transit's bond index
    // 2 = not active
    // 3 = not permissioned
    // 4 = transfer permissions don't match
    // 5 = bond does not pass balance requirements
    // 6 = pre-transit requirements pass

    function preTransitRequirementsCheck(address _address, uint256 _bip32X_type, uint256 _holdIndex, uint256 _holdIndexBond, address _toChain) internal view returns (uint8 result) {
        if (getBip32X_typeAllowedForTransit(_toChain, _bip32X_type) == true) {
            if (_holdIndex != _holdIndexBond) {
                // prove that the user's safe hold is active
                if (shyftSafe.getIsActiveForHold(_address, _holdIndex) == 2 &&
                shyftSafe.getIsActiveForHold(_address, _holdIndexBond) == 2) {
                    // check if the safe hold is still timelocked
                    if (shyftSafe.getIsTimelockedForHold(_address, _holdIndex) == 2 &&
                    shyftSafe.getIsTimelockedForHold(_address, _holdIndexBond) == 2) {
                        // check if the permissions match
                        if (shyftSafe.getIsTransferPermissionedAddressForHold(_address, address(this), _holdIndex) == 2 &&
                        shyftSafe.getIsTransferPermissionedAddressForHold(_address, address(this), _holdIndexBond) == 2) {

                            //@note:@future: extending this with per-chain transit bond requirements could lead to
                            // interesting economic incentive structures.

                            //do a pre-check to see whether a bond requirement exists, and check the bond's hold if so
                            if (getBondPassesBalanceRequirementsForTransit(_address, _holdIndexBond) == true) {
                                //pre-transit requirements pass
                                return 6;
                            } else {
                                //bond does not pass balance requirements
                                return 5;
                            }
                        } else {
                            //transfer permissions don't match
                            return 4;
                        }
                    } else {
                        //not permissioned
                        return 3;
                    }
                } else {
                    //not active
                    return 2;
                }
            } else {
                //hold index cannot equal the transit's bond index
                return 1;
            }
        } else {
            //token type not allowed for transit
            return 0;
        }
    }

    //@note: this function requires some variables that would appear to be extant (address, validTimelyBlockNumber)
    // this is because these are the variables that can are read for via confirmed transaction receipts.

    // returns:
    // 0 = msg.sender is not address specified
    // 1 = amount and value don't match
    // 2 = pre-transfer requirements don't check out
    // 3 = transferFrom did not succeed
    // 4 = could not set hold to destructible
    // 5 = could not burn hold
    // 6 = asset set up for transmission

    function transitAsset(  address _address,
                            uint256 _amount,
                            uint256 _bip32X_type,
                            uint256 _holdIndex,
                            uint256 _holdIndexBond,
                            address _toChainId) public returns (uint8 result) {
        if (msg.sender == _address) {
            // make sure that _amount and _bip32X_type match, these will be used later.
            if (_bip32X_type == shyftSafe.getBip32XTypeForHold(msg.sender, _holdIndex) && _amount == shyftSafe.getAmountForHold(msg.sender, _holdIndex)) {
                // prove that the user's safe hold is active
                if (preTransitRequirementsCheck(msg.sender, _bip32X_type, _holdIndex, _holdIndexBond, _toChainId) == 6) {
                    // safe hold is still timelocked, and the Bridge has permission to move the asset, so make a deposit
                    // bound for a particular chain.

                    //transfer the safe hold from the user to the Bridge.
                    uint8 transferFromResult;
                    uint256 bridgeNewHoldIndex;
                    (transferFromResult, bridgeNewHoldIndex) = shyftSafe.transferFromSafeHold(msg.sender, _holdIndex);

                    // make sure the transfer completes successfully
                    if (transferFromResult == 6) {
                        //@note:@todo:@future: in order to prevent defaults such as the Bridge not actually
                        // transmitting a message, either this Bridge needs to know about subsequent block headers
                        // that include more recent commit messages, or the user has to prove their hold is expired
                        // on another blockchain.

                        // this can be accomplished by proving:
                        // a. that the user has a bridge meta-header hash on *any blockchain*
                        // b. that the user can prove that this header transaction was signed by the bridge agent(s)
                        // c. that the user's transaction, given by the transaction hash they'd need to withdraw, does not
                        //    exist within the other blockchain.

                        //@note: at the moment, we simply trigger a two-step task to set the Hold to 'destructible' and
                        // then burn the tokens on the source chain, creating on the destination chain later.

                        // 2 step process, first mark the new safe hold owned by the Bridge to destructible

                        uint8 setSafeHoldDestructibleResult = shyftSafe.setSafeHoldDestructible(bridgeNewHoldIndex);
                        if (setSafeHoldDestructibleResult == 2) {
                            uint8 burnResult = shyftSafe.burnSafeHold(bridgeNewHoldIndex);

                            // then burn the hold, & the associated tokens within
                            if (burnResult == 5) {
                                emit EVT_TransitAsset(shyftSafeAddress, _toChainId, msg.sender, _bip32X_type, _amount, address(this));

                                //asset set up for transmission
                                return 6;
                            } else {
                                //could not burn hold
                                //@note: must revert here, since values were modified downstream in the inherited contracts.
                                revert();
//                                return 5;
                            }
                        } else {
                            //could not set hold to destructible
                            //@note: must revert here, since values were modified downstream in the inherited contracts.
                            revert();
//                            return 4;
                        }
                    } else {
                        //transferFrom did not succeed
                        //@note: must revert here, since values were modified downstream in the inherited contracts.
                        revert();
//                        return 3;
                    }
                } else {
                    //pre-transfer requirements don't check out
                    return 2;
                }
            } else {
                //amount and value don't match
                return 1;
            }
        } else {
            //msg.sender is not address specified
            return 0;
        }
    }


    // @dev: Prepares the withdrawal of an asset in a destination chain
    //
    // @params _bip32X_type, asset user wants to withdraw.
    // @param _amount, amount of the asset user wants to withdraw.
    // @param _fromChain, the chain on which user Transited the asset.
    // @param _txRoot, the transaction root of the block in the "from" chain in which the Transit was made.
    // @param _path, path in which the Transited transaction block resides in. It is generated off-chain.
    // @param _parentNodes, list of parent nodes in the Transit transaction.
    // @param _rlpTransitTx, rlp-encoded Transit transaction data.

    //returns:
    // 0 = could not confirm tx parameters
    // 1 = merkle tree proof failed
    // 2 = signature check failed
    // 3 = proof already prepared
    // 4 = proof is now prepared


    function prepareWithdrawalOfAsset(  uint256 _amount,
                                        uint256 _bip32X_type,
                                        address _fromChain,
                                        bytes32 _txRoot,
                                        bytes memory _path,
                                        bytes memory _parentNodes,
                                        bytes memory _rlpTransitTx) public returns (uint8 result) {
//        debugRLPTransitTx(_rlpTransitTx);

        //confirm that the transaction has the proper amount and bip32X type.
        if (confirmTxContractParameters(_rlpTransitTx, msg.sender, _amount, _bip32X_type) == 1) {
            // Prove the transaction root;
            // If on an EVM-compatible chain, use MerklePatriciaProof,
            // otherwise, it should be sufficient to use prove.
            bool merkleTreeProofResult = MerklePatriciaProof.verify(_rlpTransitTx, _path, _parentNodes, _txRoot);

            if (merkleTreeProofResult == true) {
                bytes32 rlpTransitTxHash = keccak256(abi.encodePacked(_rlpTransitTx));

                Withdrawal storage userWithdrawal = userWithdrawals[msg.sender][rlpTransitTxHash];
                if (userWithdrawal.hasBeenSetup == false) {

                    userWithdrawal.hasBeenSetup = true;
                    userWithdrawal.bip32X_type = _bip32X_type;
                    userWithdrawal.fromChain = _fromChain;
                    userWithdrawal.safeAddress = shyftSafeAddress;
                    userWithdrawal.amount = _amount;
                    userWithdrawal.txRoot = _txRoot;
                    userWithdrawal.withdrawalPrepareBlockNumber = block.number;

                    emit EVT_PreparedWithdrawal(msg.sender, _bip32X_type, _amount, _txRoot);

                    //proof is now prepared
                    return 3;
                } else {
                    //proof already prepared
                    return 2;
                }
            } else {
                //merkle tree proof failed
                return 1;
            }
        } else {
            //could not confirm tx parameters
            return 0;
        }
    }

    function proveWithdrawalReceipt(    bytes32 _receiptsRoot,
                                        bytes memory _rlpTransitTx,
                                        bytes memory _receiptPath,
                                        bytes memory _receiptParentNodes,
                                        bytes memory _rlpTransitReceipt) public returns (uint8 result) {
        // elizabeth:TODO: prove receipts root
        // need to extend util/txProof.js to generate proofs for receipts trie as well as tx trie
        bool receiptProofResult = MerklePatriciaProof.verify(_rlpTransitReceipt, _receiptPath, _receiptParentNodes, _receiptsRoot);

//        emit TryProveReceipt(_receiptsRoot);

        // TODO: make sure that status is 1
        if (receiptProofResult == true) {
            bytes32 rlpTransitTxHash = keccak256(abi.encodePacked(_rlpTransitTx));
            Withdrawal storage userWithdrawal = userWithdrawals[msg.sender][rlpTransitTxHash];
            userWithdrawal.receiptsRoot = _receiptsRoot;

            emit EVT_ProveWithdrawalReceipt(msg.sender, _receiptsRoot);

            return 1;
        } else {
            return 0;
        }
    }

    //results:
    // 0 = v,r,s doesn't match
    // 1 = amount/bip32X_type doesn't match
    // 2 = confirmed transaction parameters

    function confirmTxContractParameters(bytes memory _rlpTransitTx, address _address, uint256 _amount, uint256 _bip32X_type) internal returns(uint8 result) {
        RLP.RLPItem memory item = RLP.toRLPItem(_rlpTransitTx);
        RLP.RLPItem[] memory values = RLP.toList(item);

//        ** transitAsset **
//        address _address,
//        uint256 _amount,
//        uint256 _bip32X_type,
//        uint256 _holdIndex,
//        uint256 _holdIndexBond,
//        address _toChainId

        bytes memory input = RLP.toBytes(values[5]);

        // skip 2 bytes ('0x') + 4 (methodID) initially.
        address proofAddress = address(BytesLib.toUint(BytesLib.slice(input, 6, 32), 0));

        //decodes amount & bip32X_type from "input" in tx.
        uint256 proofAmount = BytesLib.toUint(BytesLib.slice(input, 6 + 32, 32), 0);
        uint256 proofBip32XType = BytesLib.toUint(BytesLib.slice(input, 6 + 64, 32), 0);
//        uint32 holdIndex = BytesLib.toUint(BytesLib.slice(input, 6 + 96, 32), 0);
//        uint32 holdIndexBond = BytesLib.toUint(BytesLib.slice(input, 6 + 128, 32), 0);
        address proofToChain = address(BytesLib.toUint(BytesLib.slice(input, 6 + 160, 32), 0));

//        bytes memory proofSlice = BytesLib.slice(input, 6, 32);

        emit EVT_confirmTxContractParameters(_address, _amount, _bip32X_type, proofAddress, proofAmount, proofBip32XType, address(this), proofToChain);

        //confirm the parameters match, and the "toChain" is this smart contract's address
        if (proofAddress == _address && proofAmount == _amount && proofBip32XType == _bip32X_type && proofToChain == address(this)) {
            //confirmed transaction parameters
            return 1;
        } else {
            //amount/bip32X_type doesn't match
            return 0;
        }
    }

//    function debugRLPTransitTx(bytes _rlpTransitTx) public {
//        RLP.RLPItem memory item = RLP.toRLPItem(_rlpTransitTx);
//        RLP.RLPItem[] memory values = RLP.toList(item);
//
