    //returns:
    // false = number of block requirements not passed
    // true = number of block requirements passed

    function isBlockConfirmationBlocksRequiredConfirmed(uint256 _referenceBlockNumber, uint256 _requiredBlocks) public view returns (bool result) {
        if (block.number - _referenceBlockNumber >= _requiredBlocks) {
            //number of block requirements passed
            return true;
        } else {
            //number of block requirements not passed
            return false;
        }
    }

    // @dev: This function lets the user withdraw their assets into their ShyftSafe on the new chain
    // @param _proof, Merkle proof.
    // @param _blockNum, block number of the block containing the Transit on the bridged chain.
    // @param _timestamp, timestamp of the block containing the Transit on the bridged chain.
    // @param _previousHeader, modified header of the block containing the Transit.
    // @param _rootIndex, index of the header root on the origin chain.
    // @param _rlpTransitTx, the rlp Transit transaction to create a hash from.


    //returns:
    // 0 = withdrawal cannot be processed
    // 1 = confirmation time has not been reached
    // 2 = signature check failed
    // 3 = invalid proof
    // 4 = could not mint safe hold
    // 5 = processed withdrawal

    function withdrawAsset( bytes memory _proof,
                            uint256 _blockNum,
                            uint256 _timestamp,
                            bytes32 _previousHeader,
                            uint _rootIndex,
                            bytes memory _rlpTransitTx) public returns (uint8 result, uint256 holdIndex) {

        bytes32 rlpTransitTxHash = keccak256(abi.encodePacked(_rlpTransitTx));

        Withdrawal storage userWithdrawal = userWithdrawals[msg.sender][rlpTransitTxHash];

//        emit EVT_withdrawAsset(userWithdrawal.hasBeenSetup, userWithdrawal.rlpWithdrawTx, userWithdrawal.withdrawalPrepareBlockNumber, block.timestamp, confirmationBlocksRequired[address(this)]);
        //check to make sure withdrawal has been setup and has not already been processed.
        if (userWithdrawal.hasBeenSetup == true) {
            uint256 blocksRequired = tokenChains[userWithdrawal.fromChain].confirmationBlocksRequired[userWithdrawal.bip32X_type];

            if (isBlockConfirmationBlocksRequiredConfirmed(userWithdrawal.withdrawalPrepareBlockNumber, blocksRequired)) {
                // prove that the user has the correct header
                // elizabeth:TODO: add receipts root to this, prove that correct receipts root is also included in header
                bytes32 leaf = keccak256(abi.encodePacked(_previousHeader, _timestamp, _blockNum, userWithdrawal.txRoot, userWithdrawal.receiptsRoot));
                //bytes32 leaf = keccak256(abi.encodePacked(_previousHeader, _timestamp, _blockNum, userWithdrawal.txRoot));

                emit EVT_WithdrawTest(userWithdrawal.receiptsRoot, leaf);

                if (prove(_proof, leaf, roots[userWithdrawal.fromChain][_rootIndex]) == true) {
                    userWithdrawal.hasBeenProcessed = true;

                    //sets the expiry time to zero to allow immediate accessibility.
                    uint64 safeExpiryTime = 0;
                    // mint new tokens to the _address
                    // @note: *needs to be linked in ShyftSafe to accept this bridge address as permissioned to mint*
                    uint8 mintSafeHoldResult;
                    uint256 mintHoldIndex;
                    (mintSafeHoldResult, mintHoldIndex) = shyftSafe.mintSafeHold(msg.sender, userWithdrawal.amount, userWithdrawal.bip32X_type, safeExpiryTime);

                    if (mintSafeHoldResult == 2) {
                        // emit event for withdrawal

                        emit EVT_WithdrawAsset(shyftSafeAddress, userWithdrawal.fromChain, msg.sender, userWithdrawal.bip32X_type, userWithdrawal.amount);

                        //processed withdrawal
                        return (4, mintHoldIndex);
                    } else {
                        //could not mint safe hold
                        //@note: must revert here, since values were modified downstream in the inherited contracts.
                        revert();
//                        return (3, 0);
                    }
                } else {
                    //invalid proof
                    return (2, 0);
                }
            } else {
                //confirmation # of blocks has not been reached
                return (1, 0);
            }
        } else {
