    // @dev: Verifies a Merkle proof proving the existence of a leaf in a merkle tree.
    //
    // @param _proof Merkle proof containing sibling hashed on the branch from the leaf to the root
    // @param _root Merkle root
    // @param _leaf Leaf of Merkle tree
    function prove(bytes memory _proof, bytes32 _leaf, bytes32 _root) public pure returns (bool) {
      require(_proof.length % 32 == 0);

      bool appendRight;
      uint i;
      bytes32 computedHash = _leaf;
      bytes32 proofElement;
      bytes32 indicators;

      assembly {
        indicators := mload(add(_proof, 32))
      }

      for(i = 64; i <= _proof.length; i += 32){
          assembly {
              proofElement := mload(add(_proof, i)) // get proof element
              appendRight := byte(31, and(indicators, 1)) // mask everything except last bit, get direction on which to append
          }

          indicators = indicators >> 1;

          if(appendRight) {
              computedHash = keccak256(abi.encodePacked(computedHash,proofElement));
          } else {
              computedHash = keccak256(abi.encodePacked(proofElement,computedHash));
          }
      }

      return computedHash == _root;
    }

