    // @dev: Returns the number of signatures that meet the threshold.
    //
    // @param _root, _root that is being added
    // @param _chainId, the chain from which the _root comes from
    // @param _start, the beginning block header number from which the root starts from
    // @param _end, the last block header number from which the roots ends
    // @param _sig, byte array of signatures to check
    function checkSignatures(bytes32 _root, address _chainId, uint256 _start, uint256 _end, bytes memory _sig)
    public view returns (uint) {
      require(_sig.length%65 == 0);

      bytes memory prefix = "\x19Ethereum Signed Message:\n32";

      bytes32 h = keccak256(abi.encodePacked(_root,_chainId,_start,_end));

      bytes32 r;
      bytes32 s;
      uint8 v;

      uint sigs;

      for(uint i = 0; i < _sig.length; i += 65) {
        assembly {
            r := mload(add(_sig, add(32, i)))
            s := mload(add(_sig, add(64, i)))
            v := byte(0, mload(add( _sig, add(96, i) ) ) )
        }

        //emit Sig(r,s,v);
        address relayerAddrTmp = ecrecover(keccak256(abi.encodePacked(prefix,h)),v,r,s);
        if(isInRelayerSet(relayerAddrTmp)) {
          sigs++;
        }
      }

      return sigs;
    }

