pragma solidity ^0.7.1;
//SPDX-License-Identifier: UNLICENSED
import "./Constants/TokenConstants.sol";

import "./Libraries/SafeMath.sol";
import "./Interfaces/IShyftSafe.sol";

import "./ShyftSafeManagement.sol";

/// @dev |
///      | Shyft Safe is the contract & interface definition to add "Safe-able" asset classes to a user's portfolio.
///      | This is a data management layer that collates enough data on-chain for the Bridge to prove transferability.
///      | All transfers and justification for withdrawal can be proven on-chain.
///      |
///      | The general condition is that there is a list of unproven proofs, including those that the bridge collates via
///      | observation of other blockchains. All cross-blockchain requests are queued in the user's "safe keeping" structure
///      | and the bridge has the responsibility to process them and approve. All proofs can be proven on the Shyft blockchain's
///      | evm architecture.
///      |
///      | The general order of things is that when a block is confirmed by consensus, the bridge will forward a merkle-trie
///      | proof block to the ring which is composed of transactions occurring on other blockchains that pertains to Shyft.
///      | these are cleared transactions, currently waiting in Shyft escrow systems.
///      |
///      | These transactions are processed in every block, with block times set to enable this behaviour potentially filling
///      | up empty space within a block with these transactions at a rate dependant on the user (so users could still "rush"
///      | a Shyft Safe synchronization block.)

// maintenance of the trust channels should be equivalent

//@note:@todo: finish definition

abstract contract ShyftSafe is IShyftSafe, TokenConstants {
    event EVT_heldSafeBalanceForType(address _address, uint256 indexed amount, uint256 indexed bip32X_type, uint256 safeExpiryTime, uint256 indexed holdIndex);
    event EVT_freedSafeHold(address _address, uint256 holdIndex, uint256 numRemainingHolds, HoldStatus holdStatus, uint256 freedAmount, uint256 freedBip32X_type, uint256 currentBlockTime, uint256 safeExpiryTime);
    event EVT_burntSafeHold(address _address, uint256 holdIndex, uint256 numRemainingHolds, HoldStatus holdStatus, uint256 freedAmount, uint256 freedBip32X_type, uint256 currentBlockTime, uint256 safeExpiryTime);
    event EVT_mintSafeHold(address _mintAddress, address _address, uint256 indexed amount, uint256 indexed bip32X_type, uint256 safeExpiryTime, uint256 indexed holdIndex);
    event EVT_freedSafePreservation(uint256 amount, uint256 bip32X_type, uint256 blockTimestamp, uint256 holdSafeExpiryTime);
    event EVT_safeHoldStillTimelocked(uint256 holdIndex, uint256 numRemainingHolds, uint256 currentBlockTime, uint256 safeExpiryTime);
    event EVT_preserveSafeBalanceForType(uint256 amount, uint256 bip32X_type, uint256 preservationValue);

    event EVT_setHoldDestructible(address _address, uint256 holdIndex, uint256 keepHoldArrayLength);

//    event EVT_checkForTimelock(address _address, uint256 holdIndex, uint256 safeExpiryTime, uint256 blockTimestamp);

    using SafeMath for uint256;

//    enum ProofStatus { Unknown, Dirty, Clean }
    enum HoldStatus { Unknown, Holding, NoneHeld }

//    struct safeProofMap {
//        //hash of RLP
//        bytes32 proofHash;
//
//        //RLP of proof
//        bytes proof;
//
//        bytes publicMetadata;
//        bytes encryptedMetadata;
//
//        uint256 amount;
//        uint256 bip32X_type;
//
//        bool proven;
//    }

//    struct safeKeyMap {
//        //key name, key class, key field. RLP encoded.
//        bytes publicMetaData;
//    }

    struct safeHold {
        bool active;
        bool preserved;

        bool permissionedTransfer;

        uint256 amount;
        uint256 bip32X_type;

        uint256 safeExpiryTime;

        mapping(address => bool) transferPermissionedAddresses;

        bool isDestructible;
    }

    struct safeKeep {
//        //hash of the public key value pair within the Safe engine, for incoming addresses in the proofs.
//        mapping(bytes32 => safeKeyMap) keyStorageMap;
//
//        //array of all proofs.
//        safeProofMap[] allProofs;

//        uint32 indexOfFirstDirtyProof;
//        uint32 numDirtyProofs;

        //@note:@here:@todo:@next:@wallet:
        //this hold array will grow and be managed by the wallet in terms of releasing funds back into the
        //main shyft safe pool.
        //speaking of transiting, the general strategy should be to assign held tokens into transit buckets
        //on a hold bucket->transit bucket basis. otherwise other sorting and compilation will be needed.
        safeHold[] holdArray;

        //using a doubly linked list for this, in order to access the previous and next indexes in the linked list.
        mapping(uint256 => uint256) holdNextLinkMapping;
        mapping(uint256 => uint256) holdPrevLinkMapping;

        //mapping for checks of whether the trust anchor previously existing for this identifiedAddress.
//        mapping(uint32 => bool) allTrustAnchorIndexExistsMapping;

        //@note: I'm using underscores because I want to have easily readability when the camel case variable names get
        // this long.

        uint256 currentHoldLinkedList_length;
        uint256 currentHoldLinkedList_zerothIndex;
        uint256 currentHoldLinkedList_finalIndex;

        //bip32X_type->balance
        mapping(uint256 => uint256) holdValuationMap;
        mapping(uint256 => uint256) preservationValuationMap;

        uint256 nextHoldExpiry;

        HoldStatus holdStatus;
    }

    mapping(address => safeKeep) safeKeeping;

    //@note:@here:@todo: this is erc20/223 stuff
    // system should be backwards compatible, basic table:
    //
    // (all bip32 token types follow)
    // system should be backwards compatible, basic table:
    // 0 = Bitcoin
    // 60 = Ethereum
    // 61 = Ethereum Classic
    // 137 = Rootstock
    //  ..
    // ShyftTokenType = SHYFT = 7341

    //address->bip32X_type->balance
    mapping(address => mapping(uint256 => uint256)) safeBalances;

    //@wallet: security measures require the user to manually disable burning of tokens.
    mapping(address => bool) burningDisabled;

//    ProofStatus proofStatus;

//    uint16 maxBridgeProcessingLimit;

    //@note:@deploy:
    //make sure to set this to use the shyft bridge and ring on whatever platform deployed to. might need to
    //create multiple versions of this file and deploy w/ a script.

    //this is the address of the Shyft bridge on this blockchain, on initialization steps.
    address public bridgeAddress = address(0);
    mapping(address => bool) safeTokenCreationPermissions;

    address public owner;
    uint256 nativeBip32X_type;

    constructor(uint256 _nativeBip32X_type) {
        owner = msg.sender;

        nativeBip32X_type = _nativeBip32X_type;
    }

    using SafeMath for uint256;

    //returns
    // true = is bridge
    // false = is not bridge
    function isBridge(address _addressToCheck) public view override returns (bool result) {
        if(bridgeAddress == _addressToCheck) {
            //is bridge
            return true;
        } else {
            //is not bridge
            return false;
        }
    }

    function setBridge(address _addr) public override {
        //can only do this once.
        require(bridgeAddress == address(0));
        require(_addr != address(0));
        require(msg.sender == owner);

        bridgeAddress = _addr;
        safeTokenCreationPermissions[bridgeAddress] = true;
    }

    function getShyftTokenType() public pure returns (uint256 result) {
        return ShyftTokenType;
    }

    function getNativeTokenType() public view returns (uint256 result) {
        return nativeBip32X_type;
    }

    function getSafeBalance(address _address) public view returns (uint256 balance)  {
        return safeBalances[_address][ShyftTokenType];
    }

    function getSafeBalance(address _address, uint256 bip32X_type) public view returns (uint256 balance)  {
        return safeBalances[_address][bip32X_type];
    }

    /**************** Shyft Safe preserves & holds management ****************/

    //@note:@here:@todo:@next:@critical: I think there might need to be a secondary layer here for a pool of Safe'd assets
    // that can be sent a message to release the timelock in 24hours, with a kill switch from the same address to block the
    // transfer.
    //
    // optimal process would be:
    // 1) user stores [total - minimum allowance] funds in Safe pool ( wallet doing this by default )
    // 2) user triggers message that allows funds to be unlocked after 1 day to the blockchain.
    // 3) if user notices that someone else is about to unlock their funds (private key theft), they can send a message
    //    to the blockchain that prevents the funds from being released.
    // 4) with the funds in Safe, they can be sent to other blockchains even without being released.
    //
    // in the future some way of sending Safe'd funds to arbitration (or at least a contract that can lock down with
    // an Administrable interface with multiple signatures.)

    //@note:@here: the following two functions utilize doubly-linked lists in order to avoid needing to reorder an array.

    //@note:@todo:@next:@future: need to write complete tests for these two functions too see how the doubly-linked list responds
    // in all cases (1, 2. 3 entries, what happens when first, middle, or last entry are removed from holds.)

    //returns:
    // 1 == preserved asset

    function preserveSafeBalanceForType(uint256 _amount, uint256 _bip32X_type) public returns (uint256 holdIndex) {
        //@note: this works because functional inheritance overriding works in solidity. thus this will call the
        // function of the contract that overrides overload_doHoldSafe after inheriting ShyftSafe.
        uint8 canHoldSafeResult;
        uint256 remainingBalance;
        (canHoldSafeResult, remainingBalance) = child_overload_doHoldSafe(_amount, _bip32X_type);

        //@note:@here:@todo: checks for payments pending, allowances, and the like.
        //if (remainingBalance > )

        //@note: in this context, the inheriting contract overriding function is going to return a 1 for success
        // and zero for failure.
        if (canHoldSafeResult == 1) {
            safeKeep storage keep = safeKeeping[msg.sender];

            keep.preservationValuationMap[_bip32X_type] = keep.preservationValuationMap[_bip32X_type].add(_amount);

            emit EVT_preserveSafeBalanceForType(_amount, _bip32X_type, keep.preservationValuationMap[_bip32X_type]);

            //preserved asset.
            return 1;
        } else {
            //could not free hold
            //@note: reverts, since transactional event has occurred
            revert();
            // return 0;
        }
    }

    //results:
    // 0 = preservation valuation less than amount
    // 3 = freed preserve properly

    // @note: could not free preserve reverts, since transactional event has occurred

    function freeSafePreservation(uint256 _amount, uint256 _bip32X_type) public returns (uint8 result) {
        safeKeep storage keep = safeKeeping[msg.sender];

        if (keep.preservationValuationMap[_bip32X_type] >= _amount) {
            uint8 freeSafeResult;
            uint256 remainingBalance;

            (freeSafeResult, remainingBalance) = child_overload_doFreeSafe(_amount, _bip32X_type);

            if (freeSafeResult == 1) {
                //@note: blocktime cannot be cheated by miners by more than 900 seconds.
                // https://github.com/ethereum/wiki/blob/c02254611f218f43cbb07517ca8e5d00fd6d6d75/Block-Protocol-2.0.md
                uint256 newHoldTime = block.timestamp + (2 * 24 * 60 * 60);

                // place a new hold on this address representing the amount freed from the preservation.
                uint8 placeSafeHoldResult = placeSafeHold(msg.sender, _amount, _bip32X_type, newHoldTime);

                if (placeSafeHoldResult == 1) {
                    keep.preservationValuationMap[_bip32X_type] = keep.preservationValuationMap[_bip32X_type].sub(_amount);

                    emit EVT_freedSafePreservation(_amount, _bip32X_type, block.timestamp, newHoldTime);

                    //freed preserve properly
                    return 3;
                } else {
                    //cannot place hold
                    //@note: reverts, since transactional event has occurred
                    revert();
//                    return 2;
                }
            } else {
                //could not free preserve
                //@note: reverts, since transactional event has occurred
                revert();
                //return 1;
            }
        } else {
            //preservation map less than amount
            return 0;
        }
    }

    //@note: places a hold on a specific asset type.

    function placeSafeHold( address _address,
                            uint256 _amount,
                            uint256 _bip32X_type,
                            uint256 _safeExpiryTime) internal returns (uint8 result) {
        safeKeep storage keep = safeKeeping[_address];

        //update the hold valuation map.
        keep.holdValuationMap[_bip32X_type] = keep.holdValuationMap[_bip32X_type].add(_amount);

        keep.holdArray.push();

        uint256 holdArrayLength = keep.holdArray.length;

        safeHold storage newHold = keep.holdArray[holdArrayLength - 1];
        newHold.amount = _amount;
        newHold.bip32X_type = _bip32X_type;
        newHold.safeExpiryTime = _safeExpiryTime;
        newHold.active = true;

        //set up the linked list to allow for array accessibility without needing to rearrange or delete old
        // members of the array.
        if (keep.holdStatus != HoldStatus.Holding) {
            keep.holdStatus = HoldStatus.Holding;

            //set up the zeroth address of the doubly-linked list (the first entry into the doubly-linked list).
            keep.currentHoldLinkedList_zerothIndex = holdArrayLength - 1;
            //and set the final index to this as well for further additions.
            keep.currentHoldLinkedList_finalIndex = holdArrayLength - 1;

            keep.currentHoldLinkedList_length = 1;
        } else {
            //update the prev index of this index in the doubly-linked list to point to this (previously final) index.
            keep.holdPrevLinkMapping[holdArrayLength - 1] = keep.currentHoldLinkedList_finalIndex;
            //update the final index in the doubly-linked list to point to this index.
            keep.holdNextLinkMapping[keep.currentHoldLinkedList_finalIndex] = holdArrayLength - 1;
            //and set the final index to this as well for further additions.
            keep.currentHoldLinkedList_finalIndex = holdArrayLength - 1;

            keep.currentHoldLinkedList_length++;
        }

        return 1;
    }

    //@note: removes a hold on a specific hold. there are two valid paths can either prove ownership of message structure

    //returns:
    // 0 = could not remove safe hold
    // 1 = removed safe hold

    function removeSafeHold(address _address, uint256 _holdIndex) internal returns (uint8 result) {
        safeKeep storage keep = safeKeeping[_address];
        safeHold storage hold = keep.holdArray[_holdIndex];

        //@note: make sure hold is active, then check for one of two cases:
        // a) the address is the message sender (thus proving ownership of the message structure)
        // b) that the hold is already set to transfer, and thus needs to be cleaned up.

        if (hold.active && (_address == msg.sender || hold.permissionedTransfer == true)) {
            //in both subsequent cases, the total size of the doubly-linked list decreased by one.
            keep.currentHoldLinkedList_length--;

            //and similarly, in both cases this hold is marked inactive.
            hold.active = false;

            //update the hold valuation map.
            keep.holdValuationMap[hold.bip32X_type] = keep.holdValuationMap[hold.bip32X_type].sub(hold.amount);

            //check if this hold is the zeroth (first) index within the doubly-linked list.
            if (_holdIndex == keep.currentHoldLinkedList_zerothIndex) {
                //check if this hold is also the final index within the doubly-linked list.
                if (_holdIndex == keep.currentHoldLinkedList_finalIndex) {
                    //in this case, no remapping is necessary.
                } else {
                    //otherwise, set the zeroth index to be the next index in the doubly-linked list.
                    keep.currentHoldLinkedList_zerothIndex = keep.holdNextLinkMapping[_holdIndex];
                }
            } else {
                //check if this hold is the final index within the doubly-linked list.
                if (_holdIndex == keep.currentHoldLinkedList_finalIndex) {
                    //if so, set final link in the doubly-linked list to be the the previous link of this index.
                    keep.currentHoldLinkedList_finalIndex = keep.holdPrevLinkMapping[_holdIndex];
                } else {
                    //if not, this link is in between the zeroth and final index within the doubly-linked list.
                    // in this case, set the previous link to have its next link to be the next link of the
                    // current hold. the final index of this doubly-linked list does not change.

                    uint256 prevLink = keep.holdPrevLinkMapping[_holdIndex];
                    uint256 nextLink = keep.holdNextLinkMapping[_holdIndex];

                    keep.holdPrevLinkMapping[nextLink] = prevLink;
                    keep.holdNextLinkMapping[prevLink] = nextLink;
                }
            }

            //if there's zero entries left within the doubly-linked list, the keep status is set to NoneHeld.
            if (keep.currentHoldLinkedList_length == 0) {
                keep.holdStatus = HoldStatus.NoneHeld;
            }

            //removed safe hold
            return 1;
        } else {
            //could not remove safe hold
            return 0;
        }
    }

    //returns:
    // n = holdIndex

    function holdSafeBalanceForType(uint256 _amount, uint256 _bip32X_type, uint256 _safeExpiryTime) public returns (uint256 holdIndex) {
        //@note: this works because functional inheritance overriding works in solidity. thus this will call the
        // function of the contract that overrides overload_doHoldSafe after inheriting ShyftSafe.

        uint8 canHoldSafeResult;
        uint256 remainingBalance;
        (canHoldSafeResult, remainingBalance) = child_overload_doHoldSafe(_amount, _bip32X_type);

        //@note:@here:@todo:@future: checks for payments pending, allowances, and the like.
        //if (remainingBalance > )

        //@note: in this context, the inheriting contract overriding function is going to return a 1 for success,
        // and zero for failure.
        if (canHoldSafeResult == 1) {
            uint8 placeSafeHoldResult = placeSafeHold(msg.sender, _amount, _bip32X_type, _safeExpiryTime);

            if (placeSafeHoldResult == 1) {
                safeKeep storage keep = safeKeeping[msg.sender];

                emit EVT_heldSafeBalanceForType(msg.sender, _amount, _bip32X_type, _safeExpiryTime, keep.holdArray.length - 1);

                //hold index
                return keep.holdArray.length - 1;
            } else {
                //must revert here, since values were modified downstream in the inherited contracts.
                revert();
                //return 1;
            }
        } else {
            //must revert here, since values were modified downstream in the inherited contracts.
            revert();
            //return 0;
        }
    }

    function holdSafeBalanceForTypeWithPermissions(uint256 _amount, uint256 _bip32X_type, uint256 _safeExpiryTime, address[] memory _transferPermissionedAddresses) public returns (uint256 result) {
        //@note: this will immediately revert() if it's an invalid hold.

        uint256 holdIndex = holdSafeBalanceForType(_amount, _bip32X_type, _safeExpiryTime);
        safeKeep storage keep = safeKeeping[msg.sender];

        for (uint256 i = 0; i < _transferPermissionedAddresses.length; i++) {
            keep.holdArray[holdIndex].transferPermissionedAddresses[_transferPermissionedAddresses[i]] = true;
        }

        return holdIndex;
    }

    function getNumberOfHolds(address _address) public view override returns (uint256 result) {
        safeKeep storage keep = safeKeeping[_address];
        return keep.holdArray.length;
    }

    function getAmountForHold(address _address, uint256 _holdIndex) public view override returns (uint256 result) {
        safeKeep storage keep = safeKeeping[_address];
        return keep.holdArray[_holdIndex].amount;
    }

    function getBip32XTypeForHold(address _address, uint256 _holdIndex) public view override returns (uint256 result) {
        safeKeep storage keep = safeKeeping[_address];
        return keep.holdArray[_holdIndex].bip32X_type;
    }

    function getExpiryTimeForHold(address _address, uint256 _holdIndex) public view override returns (uint256 result) {
        safeKeep storage keep = safeKeeping[_address];
        return keep.holdArray[_holdIndex].safeExpiryTime;
    }

    //returns:
    // 0 = holdIndex is out of bounds
    // 1 = address is not permissioned
    // 2 = address is permissioned

    function getIsTransferPermissionedAddressForHold(address _address, address _permissionedAddress, uint256 _holdIndex) public view override returns (uint8 result) {
        safeKeep storage keep = safeKeeping[_address];

        if (_holdIndex < keep.holdArray.length) {
            if (keep.holdArray[_holdIndex].transferPermissionedAddresses[_permissionedAddress] == true) {
                //address is permissioned
                return 2;
            } else {
                //address is not permissioned
                return 1;
            }
        } else {
            //holdIndex is out of bounds
            return 0;
        }
    }

    //returns:
    // 0 = holdIndex is out of bounds
    // 1 = hold is not timelocked
    // 2 = hold is timelocked

    function getIsTimelockedForHold(address _address, uint256 _holdIndex) public view override returns (uint8 result) {
        safeKeep storage keep = safeKeeping[_address];

        //        emit EVT_checkForTimelock(_address, _holdIndex, keep.holdArray[_holdIndex].safeExpiryTime, block.timestamp);

        if (_holdIndex < keep.holdArray.length) {
            if (keep.holdArray[_holdIndex].safeExpiryTime > block.timestamp) {
                //hold is timelocked
                return 2;
            } else {
                //hold is not timelocked
                return 1;
            }
        } else {
            //holdIndex is out of bounds
            return 0;
        }
    }

    //returns:
    // 0 = holdIndex is out of bounds
    // 1 = hold is not active
    // 2 = hold is active

    function getIsActiveForHold(address _address, uint256 _holdIndex) public view override returns (uint8 result) {
        safeKeep storage keep = safeKeeping[_address];

        if (_holdIndex < keep.holdArray.length) {
            if (keep.holdArray[_holdIndex].active == true) {
                //hold is active
                return 2;
            } else {
                //hold is not active
                return 1;
            }
        } else {
            //holdIndex is out of bounds
            return 0;
        }
    }

    //@note: 2 step process to burn tokens to increase friction for this action.

    //returns:
    // 0 = holdIndex is out of bounds
    // 1 = hold is not active
    // 2 = hold set to destructible

    function setSafeHoldDestructible(uint256 _holdIndex) public override returns (uint8 result) {
        safeKeep storage keep = safeKeeping[msg.sender];
//        emit EVT_setHoldDestructible(msg.sender, _holdIndex, keep.holdArray.length);

        if (_holdIndex < keep.holdArray.length) {
            safeHold storage hold = keep.holdArray[_holdIndex];

            if (hold.active == true) {
                hold.isDestructible = true;

                //hold set to destructible
                return 2;
            } else {
               //hold is not active
                return 1;
            }
        } else {
            //holdIndex is out of bounds
            return 0;
        }
    }

    //returns:
    // 0 = burning is disabled
    // 1 = holdIndex is out of bounds
    // 2 = hold is not able to be destructed
    // 2 = not enough funds in safe safeBalance
    // 4 = could not remove safe hold
    // 5 = burnt safe hold

    function burnSafeHold(uint256 _holdIndex) public override returns (uint8 result) {
        if (burningDisabled[msg.sender] == false) {
            safeKeep storage keep = safeKeeping[msg.sender];

            if (_holdIndex < keep.holdArray.length) {
                safeHold storage hold = keep.holdArray[_holdIndex];

                if (hold.active == true && hold.isDestructible) {

                    if (safeBalances[msg.sender][hold.bip32X_type] >= hold.amount) {
                        uint8 removeSafeHoldResult = removeSafeHold(msg.sender, _holdIndex);

                        if (removeSafeHoldResult == 1) {
                            safeBalances[msg.sender][hold.bip32X_type] = safeBalances[msg.sender][hold.bip32X_type].sub(hold.amount);

                            emit EVT_burntSafeHold(msg.sender, _holdIndex, keep.currentHoldLinkedList_length, keep.holdStatus, hold.amount, hold.bip32X_type, block.timestamp, hold.safeExpiryTime);

                            //burnt safe hold
                            return 5;
                        } else {
                            //could not remove safe hold
                            return 4;
                        }
                    } else {
                        //not enough funds in safe safeBalance
                        return 3;
                    }
                } else {
                    //hold is not able to be destructed
                    return 2;
                }
            } else {
                //holdIndex is out of bounds
                return 1;
            }
        } else {
            //burning is disabled
            return 0;
        }
    }


    //returns:
    // 0 = not permissioned to create tokens
    // 2, holdIndex = created safe hold, at index holdIndex

    function mintSafeHold(address _address, uint256 _amount, uint256 _bip32X_type, uint64 _safeExpiryTime) public override returns (uint8 result, uint256 holdIndex) {
        if (safeTokenCreationPermissions[msg.sender] == true) {
            safeBalances[_address][_bip32X_type] = safeBalances[_address][_bip32X_type].add(_amount);

            // create a new hold for the _address to reflect the change in ownership
            uint8 placeSafeHoldResult = placeSafeHold(_address, _amount, _bip32X_type, _safeExpiryTime);

            if (placeSafeHoldResult == 1) {
                safeKeep storage keep = safeKeeping[_address];

                emit EVT_heldSafeBalanceForType(_address, _amount, _bip32X_type, _safeExpiryTime, keep.holdArray.length - 1);
                emit EVT_mintSafeHold(msg.sender, _address, _amount, _bip32X_type, _safeExpiryTime, keep.holdArray.length - 1);

                //created safe hold
                return (2, keep.holdArray.length - 1);
            } else {
                //@note: reverts, since transactional event has occurred
                //could not create safe hold
                revert();
//                return (1, 0);
            }
        } else {
            //not permissioned to create tokens
            return (0, 0);
        }
    }

    //@note: this is a helper function that can be called exactly once per address, that disallows burning tokens for
    // that address entirely. smart contracts like the Bridge use the burning to accomplish provably-fair atomic blockchain
    // transactions, but normal users may disallow this.
    //
    // it is programmed as such (disallowing burning vs always allowed burning) as the user will already have a two-step
    // process for triggering a burn, as 'destructible' needs to be set in a different function for any Hold that will be
    // burnt.
    // similarly, an attacker with a stolen private key may simply choose a malicious action and trigger said process with
    // a Hold they create with the full supply of the user's funds.
    // while a master key should be able to reverse any Holds (via liquidation into the Preservation), any burn mechanism
    // is permanent, and thus it is encouraged to run this function as a user at any point.

    function setBurningDisabledPermanently() public {
        burningDisabled[msg.sender] = true;
    }

    //returns:
    // 0 = holdIndex is out of bounds
    // 1 = hold is not active
    // 2 = address is not permissioned
    // 6 = transferred funds

    function transferFromSafeHold(address _fromAddress, uint256 _holdIndex) public override returns (uint8 result, uint256 holdIndex) {
        safeKeep storage keep = safeKeeping[_fromAddress];

        if (_holdIndex < keep.holdArray.length) {
            safeHold storage hold = keep.holdArray[_holdIndex];

            if (hold.active == true) {
                if (hold.transferPermissionedAddresses[msg.sender] == true) {
                    uint8 transferResult;
                    //@note: using child_overload function here. This requires an implementable function in a child class of Shyft Safe
                    // (for example, the Shyft Kyc Contract.)
                    (transferResult, ) = child_overload_doTransferSafeFunds(_fromAddress, msg.sender, hold.amount, hold.bip32X_type);

                    if (transferResult == 1) {
                        // set the flag of permissionedTransfer to the _fromAddress hold, as it's transferred the assets
                        // correctly, and ready for a permissioned transfer.

                        hold.permissionedTransfer = true;

                        // now remove the hold from the _fromAddress accounts
                        uint8 removeSafeHoldResult = removeSafeHold(_fromAddress, _holdIndex);

                        if (removeSafeHoldResult == 1) {
                            emit EVT_freedSafeHold(msg.sender, _holdIndex, keep.currentHoldLinkedList_length, keep.holdStatus, hold.amount, hold.bip32X_type, block.timestamp, hold.safeExpiryTime);

                            // create a new hold for the msg.sender to reflect the change in ownership
                            uint8 placeSafeHoldResult = placeSafeHold(msg.sender, hold.amount, hold.bip32X_type, hold.safeExpiryTime);

                            if (placeSafeHoldResult == 1) {
                                safeKeep storage keepPermissionedAddress = safeKeeping[msg.sender];

                                emit EVT_heldSafeBalanceForType(msg.sender, hold.amount, hold.bip32X_type, hold.safeExpiryTime, keepPermissionedAddress.holdArray.length - 1);

                                //transferred funds
                                return (6, keepPermissionedAddress.holdArray.length - 1);
                            } else {
                                //@note: reverts, since transactional event has occurred
                                //could not create safe hold
                                revert();
//                                return (5, 0);
                            }
                        } else {
                            //@note: reverts, since transactional event has occurred
                            //could not free safe hold
                            revert();
//                            return (4, 0);
                        }
                    } else {
                        //@note: reverts, since transactional event has (potentially) occurred in the child function
                        //could not transfer safe funds
                        revert();
//                        return (3, 0);
                    }
                } else {
                    //address is not permissioned
                    return (2, 0);
                }
            } else {
                //hold is not active
                return (1, 0);
            }
        } else {
            //holdIndex is out of bounds
            return (0, 0);
        }
    }

